INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("133", "blueberry pancake syrup", "i've made this blueberry syrup several times, but i bet it would work well for raspberries.  it's worth a try!  ", "86", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', '5-ingredients-or-less', 'sauces', 'breakfast', 'desserts', 'condiments-etc', 'fruit', 'easy', 'vegan', 'vegetarian', 'cakes', 'dietary', 'gluten-free', 'sweet-sauces', 'free-of-something', 'berries', 'blueberries', 'taste-mood', 'sweet', 'number-of-servings', '4-hours-or-less'", "1329.7, 1.0, 1315.0, 0.0, 4.0, 0.0, 114.0", "17", "'pour the blueberries into a saucepan and crush them with a potato masher or a wooden spoon until most of the skins are broken', 'add 1 cup of water and strips of lemon peel and bring to a simmer', 'turn heat down to low and cook the berries for 5 minutes at just under a simmer', 'pour the hot berries into a strainer lined with two layers of cheesecloth and let the blueberry juice drip through', 'twist the cloth to extract all the juice', 'there should be about 2 cups', 'discard the berry pulp', 'combine the remaining 2 cups water with the sugar in a small saucepan', 'bring the mixture to a boil , stirring , until the sugar is dissolved and the mixture is clear', 'wash down the sides of the pan with a wet pastry brush , then boil the syrup , without stirring , until it reaches 260 degrees on a candy thermometer', 'add the blueberry syrup to the sugar syrup and bring the mixture to boil', 'boil for 1 minute', 'let the syrup cool , then add lemon juice to taste', 'pour the syrup into two pint jars and refrigerate if you plan to use it in a month or two', 'to can pour into canning jars with 1 / 2 inch headspace', 'process in hot water bath for 30 minutes', 'cool'", "'blueberries', 'water', 'lemons, rind of', 'sugar', 'lemon juice'", "5", "https://www.food.com/recipe/133");
