INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("141", "carrie s pizza rolls", "a favorite!", "265", "'weeknight', 'time-to-make', 'course', 'preparation', 'occasion', 'for-1-or-2', 'healthy', 'main-dish', 'oven', 'pizza', 'dietary', 'low-cholesterol', 'oamc-freezer-make-ahead', 'healthy-2', 'low-in-something', 'equipment', 'number-of-servings'", "3521.8, 166.0, 118.0, 121.0, 268.0, 194.0, 182.0", "22", "'dough: dissolve yeast with honey and 1 / 2 cup water', 'let stand 10 minutes', 'put 4 cups flour in large mixing bowl', 'stir in oil , water and yeast mixture', 'mix thoroughly', 'knead 8 to 10 minutes', 'let dough rise 1 hour until doubled', 'punch down and roll on floured surface to about 1 / 2- inch', 'cut into rectangles about 4 x 6-inches or desired size of pizza roll', 'sauce: place all ingredients in a crock-pot and simmer on low for about 2 hours', 'let cool to room temperature', 'hot sauce will make dough soggy', 'rolls: place a small amount of sauce on the dough rectangles , spreading it to cover the entire piece of dough', 'sprinkle mozzarella cheese over half of the dough', 'roll up the dough burrito-style , sealing edges with moistened fingers', 'place rolls on a cookie sheet', 'allow to rest for about 10 minutes', 'bake in a hot 500 degrees oven until dough is golden brown', 'serve rolls with a side bowl of hot pizza sauce for dipping the pizza rolls inches', 'these freeze beautifully', 'can be reheated in the microwave', 'variations: you may wish to add slices of mushroom , olive , shredded beef , pepperoni slices or whatever in addition to the mozzarella cheese inside the pizza roll'", "'dry yeast', 'water', 'honey', 'wheat', 'oil', 'garlic clove', 'tomatoes', 'tomato sauce', 'mozzarella cheese', 'parmesan cheese', 'olive oil', 'salt', 'pepper', 'oregano', 'basil'", "15", "https://www.food.com/recipe/141");
