INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("143", "cherry tomatoes on provolone garlic bread", "", "105", "'weeknight', 'time-to-make', 'course', 'preparation', 'occasion', 'side-dishes', 'dietary', 'low-carb', 'low-in-something', '4-hours-or-less'", "488.6, 59.0, 28.0, 20.0, 26.0, 49.0, 7.0", "11", "'for the tomatoes , mix tomatoes , scallions , parsley , rosemary , garlic , olive oil and vinegar in a shallow bowl', 'season with salt and pepper', 'cover bowl and let tomatoes marinate at room temperature for at least 1 hour , but preferably 3-4 hours', 'stir occasionally to distribute seasonings', 'for the bread , combine olive oil and garlic and let mixture stand for about 10 minutes so flavors can mingle', 'meanwhile , heat the broiler', 'brush one side of each bread slice with garlic and olive oil mixture and broil them oiled-side up until lightly browned', 'put a slice of provolone and a generous sprinkling of parmesan on each slice , reserving a little of the parmesan for garnish', 'set aside', 'just before serving , heat up broiler again and toast cheese until bubbly', 'serve bread in shallow bowls , spooning about 3 / 4 cup of tomatoes and marinade over each slice , garnishing with remaining parmesan'", "'cherry tomatoes', 'scallions', 'parsley', 'rosemary', 'garlic', 'extra virgin olive oil', 'balsamic vinegar', 'salt & pepper', 'bread', 'provolone cheese', 'parmesan cheese'", "11", "https://www.food.com/recipe/143");
