INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("153", "amish friendship bread and starter", "many recipes have been posted for the amish bread, but none have included the starter, so i thought i'd send this along.  happy baking!  amish friendship bread and starter", "70", "'time-to-make', 'course', 'cuisine', 'preparation', 'north-american', 'breads', 'american', 'oven', 'easy', 'dietary', 'amish-mennonite', 'northeastern-united-states', 'quick-breads', 'equipment', 'number-of-servings', '4-hours-or-less'", "615.9, 32.0, 260.0, 15.0, 16.0, 19.0, 33.0", "19", "'place one cup each sugar , milk , and flour in large glass or plastic bowl', 'cover lightly with plastic wrap and set on your counter at room temperature', 'stir with a wooden spoon everyday for 17 days', 'on day 18 do nothing', 'on days 19 , 20 , and 21 , stir', 'on day 22 , add 1 cup sugar , 1 cup milk , and 1 cup flour , and stir again', 'on days 23 , 24 , 25 , and 26 stir', 'on day 27 , add 1 cup milk , 1 cup flour , and 1 cup sugar and stir', ""it's now ready to use to make the bread"", 'give away two cups of the starter to two friends , use one to make the bread , and keep one to keep the starter going', 'when you give away a cup of the starter , give these directions for keeping it going: do not refrigerate', 'do not use metal spoon or metal bowl', 'do not cover tightly , cover loosely with plastic wrap', 'amish friendship bread: mix oil , eggs , and vanilla with the starter mix', 'in a separate bowl , mix flour , sugar , baking powder , cinnamon , salt , baking soda , vanilla pudding mix , and nuts', 'add to liquid mixture and stir thoroughly', 'pour into two large well-greased 9x5 inch loaf pans , or one bundt pan , that have been sprinkled with a mixture of cinnamon , sugar , and flour', 'sprinkle more cinnamon and sugar on the tops of the unbaked loaves', 'bake at 325f for one hour or until done'", "'sugar', 'milk', 'flour', 'oil', 'eggs', 'vanilla extract', 'baking powder', 'cinnamon', 'salt', 'baking soda', 'instant vanilla pudding', 'nuts'", "12", "https://www.food.com/recipe/153");
