INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("191", "chilies corn muffins", "", "60", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'breads', 'vegetables', 'oven', 'muffins', 'dietary', 'quick-breads', 'peppers', 'equipment'", "163.6, 8.0, 36.0, 6.0, 6.0, 3.0, 8.0", "9", "'preheat oven to 400', 'spray a standard muffin pan with cooking spray', 'in medium bowl , whisk flour , cornmeal , both sugars , baking powder and salt', 'in medium bowl , mix remaining ingredients', 'make well in dry ingredients', 'add milk mixture and stir until just blended', 'divide batter among prepared muffin cups', 'bake 15 to 20 minutes , or until top springs back when lightly pressed', 'let cool in pan 5 minutes , them loosen edges and turn out on rack and cool completely'", "'all-purpose flour', 'cornmeal', 'sugar', 'brown sugar', 'baking powder', 'salt', 'skim milk', 'vegetable oil', 'egg', 'green chilies'", "10", "https://www.food.com/recipe/191");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("192", "classic cream scones", "you're best off describing scones to a german by saying that they are like a   cross between a kuchen (cake), a plaetzchen (cookie), and a broetchen   (roll), but are very unique and not really like anything else found in   germany. my german husband *loves* this recipe:  classic cream scones   (from simply scones, by leslie weiner and barbara albright)", "85", "'weeknight', 'time-to-make', 'course', 'preparation', 'occasion', 'breads', 'oven', 'dietary', 'scones', 'quick-breads', 'taste-mood', 'sweet', 'equipment', 'number-of-servings', '4-hours-or-less'", "168.2, 12.0, 28.0, 3.0, 5.0, 24.0, 7.0", "20", "'preheat oven to 425f', 'lightly butter a baking sheet', 'in a large bowl , stir together the flour , sugar , baking powder , and salt', 'cut the butter into 1 / 2-inch cubes and distribute them over the flour mixture', 'with a pastry blender or two knives used scissors fashion , cut in the butter until the mixture resembles coarse crumbs', 'in a small bowl , stir together the cream , egg , and vanilla', 'add the cream mixture to the flour mixture and stir until combines', 'stir in the currents / raisins', 'with lightly floured hands , pat the dough into a 1 / 2-inch thickness on a lightly floured cutting board', 'using a floured 2 1 / 2-inch-diameter round biscuit cutter or a glass , cut out rounds from the dough and place them on the prepared baking sheet', 'gather the scraps together and repeat until all the dough is used', 'lightly brush the tops of the scones with the egg mixture , if desired', 'bake for 13-15 minutes , or until lightly browned', 'remove the baking sheet to a wire rack and cool for 5 minutes', 'using a spatula , transfer the scones to the wire rack to cool', 'serve warm or cool completely and store in an airtight container', 'makes about 14 scones', 'variation: add about 1 1 / 2 teaspoons of grated lemon peel to the dry ingredients', 'my variation: classic scones 280 grams all-purpose flour 65 g granulated sugar 2 teaspoons baking powder 1 / 8 teaspoon salt 110 g unsalted butter , chilled 6 tablespoons milk 1 large egg 1 teaspoon vanilla sugar 125 g raisins preheat oven to 220c', 'directions as above'", "'all-purpose flour', 'granulated sugar', 'baking powder', 'salt', 'unsalted butter', 'heavy cream', 'egg', 'vanilla extract', 'currants'", "9", "https://www.food.com/recipe/192");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("194", "classic chicken and coconut soup", "", "87", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'for-1-or-2', 'very-low-carbs', 'bisques-cream-soups', 'soups-stews', 'fruit', 'poultry', 'easy', 'nuts', 'chicken', 'dietary', 'low-calorie', 'low-carb', 'low-in-something', 'coconut', 'meat', 'chicken-breasts', 'number-of-servings', '4-hours-or-less'", "587.8, 80.0, 3.0, 34.0, 49.0, 206.0, 3.0", "13", "'bring first 3 ingredients to boil in heavy medium saucepan', 'reduce', 'heat , add chicken and simmer until almost cooked through , turning occasionally , about 12 minutes', 'transfer chicken to plate', 'cool', 'remove chicken skin and bones', 'cut meat into 1 / 2-inch pieces', 'strain soup through sieve into small saucepan', 'add 1 tbsp lime juice and jalapeno to soup and simmer until reduced to 2 cups , about 20 minutes', 'add chicken and simmer just to heat through', 'taste , adding more lime juice if desired', 'ladle soup into bowls', 'sprinkle with fresh cilantro'", "'chicken broth', 'unsweetened coconut milk', 'fresh lemongrass', 'chicken breast', 'lime juice', 'jalapeno chile', 'fresh cilantro'", "7", "https://www.food.com/recipe/194");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("195", "chinese hot and sour chicken noodle salad", "originally from ", "80", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'healthy', 'salads', 'poultry', 'asian', 'chinese', 'chicken', 'dietary', 'low-cholesterol', 'healthy-2', 'low-in-something', 'meat', 'chicken-breasts', 'pasta-rice-and-grains', '4-hours-or-less'", "340.7, 19.0, 4.0, 15.0, 31.0, 12.0, 14.0", "22", "'in a 5-6 quart pan , bring about 3 quarts of water to a boil', 'add chicken , cover and bring to a boil', 'remove from heat and let stand , covered , until meat is white in thickest part , about 20-25 min', 'lift out chicken , let cool and reserve water', 'remove and discard skin and bones', 'tear chicken into bite-size shreds', 'if made ahead , cover and chill chicken and water until next day', 'bring water to a boil and add linguine', 'boil , uncovered , until just tender to bite , 6-8 min', 'drain', 'immerse in cold water until cool , then drain well again', 'remove any debris from peppercorns', 'in a 6-8"" frying pan , toast peppercorns over med-low heat until fragrant , 2-3 min', 'shake pan often', 'pour peppercorns into a blender and whirl until finely ground', 'add chiles and oil to pan', 'cook over low heat until chiles just begin to brown , about 3 min', 'let cool and add ground pepper , vinegar , soy sauce and cilantro', 'on a shallow dish , arrange a bed of noodles', 'cover with cucumber and chicken', 'pour dressing evenly over salad and mix to blend', '- - - - - - - - - - - - - - - - - -'", "'chicken breast', 'dry linguine', 'peppercorn', 'dried hot red chile', 'salad oil', 'rice vinegar', 'soy sauce', 'fresh cilantro', 'cucumber'", "9", "https://www.food.com/recipe/195");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("196", "clam   lobster bake", "even if you don't live at the beach, you can enjoy an impressive backyard clambake. simply substitute a combination of greens and damp cheesecloth for seaweed, firewood for driftwood and heavy duty aluminum foil for the traditional sand covering.  about 12 lbs. of mixed greens (collard greens, kale, etc.) and 15 yards of cheesecloth will hold in moisture as food cooks. three rolls of heavy duty 37 1/2  ft. aluminum foil will both line and seal pit", "475", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'healthy', 'main-dish', 'seafood', 'lobster', 'dietary', 'low-sodium', 'gluten-free', 'low-saturated-fat', 'low-calorie', 'low-carb', 'free-of-something', 'low-in-something', 'shellfish', 'clams'", "990.1, 47.0, 38.0, 29.0, 214.0, 41.0, 23.0", "29", "'prepare your favorite clam chowder as an appetizer', 'serve clambake with plenty of melted butter , lemons , and watermelon', '"" detailed directions below', 'dig a bow-shaped pit 3 ft', 'in diameter and 1', '5 feet deep in the center', 'using crisscross pattern , line with double thickness of heavy duty aluminum foil , then a layer of dry rocks about the size of grapefruit', 'build a bonfire on top of rocks in the pit', 'use firewood and kindling', 'let the fire burn 1', '5 to 2 hours or until burned down and rocks are hot', 'soak corn-on-the-cob and mixed greens in cold water while fire is burning', 'wash clams and set aside', 'refrigerate lobster on ice', 'peel onions', 'remove ends', 'oil potatoes', 'once fire has burned down , work quickly to prevent rocks from losing heat', 'tamp down embers', 'on top of rocks , layer pit in the following order: half of the greens , strips of wet cheesecloth , potatoes , corn , onions , chicken halves , live lobsters and clams', 'cover food with remaining wet cheesecloth , then greens', 'cover pit with heavy duty aluminum foil', 'crimp all sides to the edges of aluminum foil used to line the pit', 'let food steam cook 40 to 60 minutes', 'remove foods immediately when done', 'cooking times will vary depending on temperature of rocks', 'peek at clams after 40 minutes', 'if they are open and lobsters are bright red , dinner is ready', 'serve with melted butter and lemon'", "'baking potatoes', 'corn', 'onions', 'chicken halves', 'live lobsters', 'clams'", "6", "https://www.food.com/recipe/196");
