INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("294", "crisp sauteed cucumbers with lemon", "crisp sauteed cucumbers with lemon", "65", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'healthy', 'side-dishes', 'vegetables', 'stove-top', 'dietary', 'low-cholesterol', 'low-in-something', 'equipment', 'number-of-servings', '4-hours-or-less'", "299.6, 10.0, 9.0, 74.0, 14.0, 18.0, 17.0", "21", "'peel the cucumbers if waxed', 'cut cucumbers in half lengthwise', 'scoop out the seeds with a spoon', 'cut them into inch chunks', 'if the cucumbers are not super-firm , place the chunks in a colander and sprinkle them with 1 tablespoon of salt', 'shake to distribute the salt', 'let cucumber drain for about 20 minutes', 'rinse and dry', 'if the cucumbers are already firm , proceed with the recipe', 'remove a few strips of zest from the lemon with a zester or vegetable peeler', 'mince enough to measure 1 teaspoon', 'cut the lemon in half through its equator and section it as you would an orange', 'set the zest and lemon sections aside', 'place the butter or oil in a large skillet over medium high heat', 'place about 2 cups flour in a bag , and , when the butter melts or the oil is hot , put a few chunks of cucumber at a time in the bag', 'shake well', 'add cucumbers to the skillet', 'repeat until all the cucumbers are used up', 'cook over medium high heat , stirring and turning occasionally , for about 10 minutes , until the exteriors of the cucumbers are crisp and the interiors are tender enough to be easily pierced with a thin-bladed knife', 'add the lemon zest and lemon sections , along with salt and pepper , and cook for 1 minute more', 'garnish with parsley and serve'", "'cucumber', 'salt', 'lemon', 'butter', 'flour', 'pepper', 'fresh parsley leaves'", "7", "https://www.food.com/recipe/294");
