INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("300", "english trifle   guy attwood", "english trifle - guy attwood", "342", "'weeknight', 'time-to-make', 'course', 'cuisine', 'preparation', 'occasion', 'low-protein', 'desserts', 'european', 'heirloom-historical', 'holiday-event', 'english', 'dietary', 'low-sodium', 'low-in-something'", "674.0, 62.0, 142.0, 8.0, 15.0, 118.0, 22.0", "28", "'this recipe was given to me by my grandmother who came over to america in 1895 from england', 'i have altered it slightly', 'as far as i know it is relatively original , or as original as any type of recipe such as this can be', 'it is not only addictive and decadent but pretty to look at as well', 'this is a special occasion dessert', 'whisk yolks in a medium saucepan', 'gradually add sugar , whisking until mixture is thick and lemon colored , about 1 or 2 minutes', 'blend in whole milk , whipping cream and cornstarch', 'mix', 'place over medium low heat and cook , stirring constantly , until mixture thickens , about 3 to 5 minutes', 'do not boil or mixture will separate', 'remove from heat and stir until slightly cooled', 'blend in vanilla and nutmeg', 'transfer to a bowl', 'repeat process for the two required for the trifle', 'assembly:', 'whip the cream as your mama or daddy taught you to do', 'in your prettiest large crystal bowl place a little less than half the whipped cream around the bottom and half way up the sides', 'break 1 / 2 of the cakes into bite sized pieces and drop randomly all over the cream', 'drizzle sherry over all', 'drop dollops of jam and slightly less than half of the drained raspberries over the cake', 'drizzle 1 / 2 of the thick but liquid custard over all', 'repeat for the next layer , reserving some raspberries for the top', 'finally , completely cover the top with the remaining whipped cream', 'drizzle a little chocolate on top in any pattern you think looks good',