INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("310", "homemade butter", "", "395", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'low-protein', '5-ingredients-or-less', 'very-low-carbs', 'condiments-etc', 'eggs-dairy', 'easy', 'dietary', 'low-sodium', 'low-carb', 'low-in-something'", "68.4, 11.0, 0.0, 0.0, 0.0, 22.0, 0.0", "10", "'place the bowl and blade of a food processor into the freezer to chill for 4 to 5 hours', 'refit the bowl and blade , turn on the food processor , and pour the cream through the feed tube', 'continue to process for about 10 minutes , until the butter has hardened around the blade , leaving the liquid that has separated from the cream in the bottom of the bowl', 'transfer the butter to a fine sieve and shake 2 or 3 times over the sink to allow any excess liquid to drain', 'put the butter in a bowl , cover , and chill for at least 1 hour before using', 'store it in the refrigerator , where it has a shelf life of about 1 week', 'while holding the ruler , grab the end of the top layer of wax paper and slowly pull it toward you', 'this will form the butter into the shape of a log', 'roll the paper around the log and chill', 'serve whole or cut off medallions as needed'", "'heavy cream'", "1", "https://www.food.com/recipe/310");
