INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("478", "forepaughs english trifle", "this recipe from forepaughs restaurant in st. paul, minnesota, a layered cake-and-berry specialty in its tall glass bowl, is a holiday show stopper.  prep time is approximate and includes chilling.  (this is an adopted recipe.  i have not prepared this particular version of trifle.)", "185", "'weeknight', 'time-to-make', 'course', 'cuisine', 'preparation', 'occasion', 'low-protein', 'desserts', 'european', 'holiday-event', 'english', 'dietary', 'low-sodium', 'low-in-something', '4-hours-or-less'", "874.5, 63.0, 270.0, 8.0, 27.0, 105.0, 38.0", "19", "'for pastry cream , in a large , heavy saucepan , combine egg yolks , 1 / 2 cup sugar and cornstarch until smooth', 'add milk and cook and stir just until mixture comes to boiling', 'remove from the heat', 'stir in butter and vanilla', 'cool , cover and chill the mixture for about 2 hours', 'fold in the whipped cream', 'for raspberry sauce , in a 4- to 5-quart dutch oven , heat raspberries over low heat until thawed', 'stir in sugar', 'cook , uncovered , over medium-low heat for 40 minutes or until thickened to consistency of egg whites', 'cool for about 30 minutes', 'sieve to remove seeds', 'cut the sponge cake into 1-1 / 2-inch pieces', 'begin layering the trifle by arranging half of the cake pieces in the bottom of a clear 3-quart glass bowl', 'sprinkle half of the sherry over cake', 'spoon 1 package of strawberries over cake to cover', 'repeat layers', 'cover and chill for up to 24 hours', 'beat the 1 cup whipping cream with the 1 tablespoon sugar until stiff peaks form', 'serve the trifle topped with the raspberry sauce , sweetened whipped cream and almonds'", "'egg yolks', 'sugar', 'cornstarch', 'milk', 'butter', 'vanilla', 'whipping cream', 'red raspberries', 'sponge cake', 'dry sherry', 'toasted sliced almonds', 'frozen strawberries'", "12", "htt