INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("604", "english muffins", "far better than any english muffins you can buy in the store, and much less expensive, too.", "145", "'weeknight', 'time-to-make', 'course', 'cuisine', 'preparation', 'healthy', 'breads', 'breakfast', 'european', 'english', 'stove-top', 'dietary', 'yeast', 'equipment', 'number-of-servings', '4-hours-or-less'", "165.2, 7.0, 5.0, 8.0, 8.0, 13.0, 8.0", "19", "'since you cook these on top of the stove , there is no need to preheat the oven', 'proof the yeast in the l / 2 cup of lukewarm milk with the sugar', 'scald the 1 cup of milk , melt the butter in it and let cool to lukewarm', 'add the salt and the slightly beaten egg to the milk-and-butter mixture', 'pour the yeast sponge into a large mixing bowl and blend in the milk-butter-and-egg mixture', 'add the flour , a little at a time , mixing together well', 'turn the dough out onto a lightly floured work surface and knead , adding more flour if necessary', 'knead until elastic and shiny', 'then place the dough in a large buttered bowl , cover with a towel and let rise for at least 1 hour , until doubled in bulk', 'meanwhile , pour the cornmeal onto a platter and set aside', 'take the dough out onto the work surface and knead again for a couple of minutes', 'roll it out with a rolling pin to a thickness of about 1 / 3"" and , with a glass or a large biscuit cutter , cut out round cakes', 'put these onto the platter with the cornmeal , press down slightly and turn to get the other side covered with cornmeal', 'place on a baking sheet and cover with a towel', 'let stand for about 20 to 30 minutes or until doubled in size', 'place a skillet over medium heat and when hot , brush with a little melted butter , turn the heat low and place about 5 to 6 muffins in the pan', 'cook for approximately 5 minutes on each side , or until they are slightly brown', 'cool on a rac