INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("606", "fabulous oxtail", "this is superb.", "223", "'weeknight', 'time-to-make', 'course', 'preparation', 'occasion', 'main-dish', 'oven', 'dietary', 'equipment', 'number-of-servings', '4-hours-or-less'", "472.1, 30.0, 126.0, 23.0, 7.0, 28.0, 16.0", "15", "'trim fat from the ox tails and cut into sections between the joints', 'peel the carrots and onions , trim the celery and cut into even-sized pieces about', 'heat the oil and butter in a frying pan and brown the oxtail well on all sides', 'transfer the meat to a large casserole', 'add the vegetables to the pan and toss regularly until they are well browned', 'add the flour and cook for 2 - 3 minutes until flour begins to brown lightly', 'add the plum jam , vinegar , sugar , beef stock , red wine and bouquet garni and stir well to make a smooth mixture', 'bring to the boil and then pour over the oxtail', 'cover and cook at 150 c for 3 hours until the meat is very tender and falling off the bone', 'taste the sauce and season with salt and pepper if needed', 'if possible , leave the casserole to stand overnight to allow the fat to rise and set', 'skim off fat and re-heat', 'serve with creamy mashed potatoes with chopped parsley', 'cheers , doreen doreen randal , wanganui', 'new zealand'", "'oxtails', 'carrots', 'onions', 'celery', 'olive oil', 'butter', 'flour', 'plum jam', 'brown sugar', 'beef stock', 'red wine', 'salt & pepper'", "12", "https://www.food.com/recipe/606");
