INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("614", "augie oliver s italian sausage", "to stuff the casings, you will need a meat grinder with a sausage horn attachment. if you don't have such a device, use the mixture to make fried patties by shaping 1/4 cup of filling into a 1/2 inch thick patty and frying in hot oil until golden brown on both sides.", "75", "'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'for-large-groups', '5-ingredients-or-less', 'very-low-carbs', 'main-dish', 'pork', 'easy', 'european', 'italian', 'dietary', 'spicy', 'gluten-free', 'high-protein', 'low-carb', 'free-of-something', 'high-in-something', 'low-in-something', 'meat', 'taste-mood', 'number-of-servings', '4-hours-or-less'", "514.6, 55.0, 0.0, 98.0, 86.0, 61.0, 0.0", "12", "'to prepare casings: let casings soak in cool water about five minutes to remove salt on outer surface and flush salt from the inside by placing one end on faucet nozzle and turn on cold tap water', 'remove casing from faucet and gently squeeze out water', 'cover rinsed casings and refrigerate until ready to use', 'first grind meat coarsely , incorporate seasonings evenly , and re-grind', 'fill the casings using the sausage horn attachment and make links by twisting the sausage where you wish the links to be', 'four inches is a good size for a regular serving , smaller links may be made for appetizer servings', 'to cook , place the sausage in large saucepan or dutch oven in a single layer', 'add a little water or broth , cover and heat', 'to a low simmer', 'maintain a low simmer until the sausage is heated through and cooked thoroughly , approximately 15 minutes', 'drain and let rest for about 15 minutes before slicing', 'serve while still warm'", "'boneless pork butt', 'salt', 'fennel seeds', 'red peppers', 'casing'", "5", "https://www.food.com/recipe/614");
