INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("644", "elderflower champagne", "", "2920", "'weeknight', 'time-to-make', 'course', 'preparation', 'occasion', 'beverages', '1-day-or-more', 'summer', 'dietary', 'gluten-free', 'seasonal', 'free-of-something', 'number-of-servings', 'presentation', 'served-cold'", "2681.8, 0.0, 2732.0, 5.0, 3.0, 0.0, 231.0", "11", "'do not wash the flower , but remove any insects and the thick stalks', 'place the sugar in a very large bowl and cover with 2 pints of boiling water', 'stir until the sugar has dissolved', 'then add 6 pints cold water , the rind and juice of the lemons , the vinegar and flowers', 'stir well', 'cover and leave , covered , for 48 hours , stirring occasionally', 'strain through a fine sieve into clean bottles with screw tops', 'leave an inch gap at the top of each bottle and screw down well', 'leave in a cool place to mature', 'nice served with ice', 'ready to drink in 6 weeks but it tastes better the longer it is left'", "'elderflower heads', 'water', 'boiling water', 'granulated sugar', 'lemons', 'white wine vinegar'", "6", "https://www.food.com/recipe/644");
