INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("651", "curried chicken muffins", "i love savoury muffins.  here's an unusual one i got off a lifestyle programme  called burke's backyard in australia.  curried chicken muffins - makes 12 - tastes good in lunch boxes", "90", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'south-west-pacific', 'breads', 'poultry', 'australian', 'oven', 'muffins', 'chicken', 'dietary', 'low-sodium', 'quick-breads', 'low-in-something', 'meat', 'chicken-thighs-legs', 'equipment', 'number-of-servings', '4-hours-or-less'", "244.3, 21.0, 9.0, 2.0, 15.0, 11.0, 7.0", "19", "'preheat oven to 190 c', 'grease a 12 hole muffin tin with butter and oil', ""make sure to grease the top of the tin well so any mix won't stick as they rise"", 'heat oil in a pan and cook the onion rings over a medium heat , stirring often', ""cook but don't brown them , adding a splash of water helps , takes 5-10 minutes"", 'put the onions aside', '3 / 4 will be used for presentation , chop the other 1 / 4', 'heat the pan again and add a little bit of oil and the chicken', 'stir until it begins to turn white and then add the curry paste', 'cook slightly , adding a splash of water to speed up the process', 'cook for a further minute then remove from the heat and stir in the yoghurt', 'let it cool slightly', 'sift flour into a large bowl from a good height to aerate it well', 'add the chopped onions , lemon juice , eggs , vegetable oil , cooled curried chicken and chopped coriander and mix well', 'spoon the mixture into the greased muffin tin and push the onion rigs on top by gently pressing in', 'bake in preheated oven for 15-20 minutes until risen and golden', 'allow to cool completely', 'good picnic food', 'enjoy !'", "'vegetable oil', 'onions', 'chicken thigh fillets', 'curry paste', 'natural yoghurt', 'self raising flour', 'eggs', 'lemon juice', 'f