INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("653", "b c  cherry and raspberry preserves", "", "215", "'lactose', 'weeknight', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'low-protein', 'healthy', '5-ingredients-or-less', 'jams-and-preserves', 'canning', 'condiments-etc', 'fruit', 'canadian', 'easy', 'kosher', 'low-fat', 'vegan', 'vegetarian', 'dietary', 'gifts', 'low-sodium', 'gluten-free', 'low-cholesterol', 'low-saturated-fat', 'egg-free', 'healthy-2', 'free-of-something', 'low-in-something', 'berries', 'raspberries', 'pitted-fruit', 'cherries', 'number-of-servings', 'technique', '4-hours-or-less'", "950.0, 3.0, 859.0, 0.0, 10.0, 0.0, 80.0", "17", "'stem and pit the cherries', 'you should have 4 cups', 'in a large bowl , stir together the cherries , raspberries and sugar', 'let stand at room temperature , stirring occasionally , for 2 hours', 'pour the fruit into a wide , shallow , nonreactive saucepan and stir in the lemon juice', 'cook over moderate heat , stirring occasionally , for 30 to 40 minutes until the mixture looks thickened and glazed', 'remove a table-spoon of the preserves to a small saucer and chill in the freezer for 5 minutes', 'run your finger through the mixture', 'if it wrinkles , it is ready to jar', 'if it is not ready , continue cooking for 5 more minutes and repeat the test', 'sterilize four 8-ounce canning jars by washing and rinsing them in the dishwasher without detergent', 'keep them warm in a 250 degrees f oven', 'pour boiling water over the jar lids to soften the rubber seals', 'ladle the hot preserves to within 1 / 2 inch of the rims of the jars', 'wipe the rims and seal with the hot lids and metal bands', 'let cool to room temperature , then refrigerate for several weeks', 'or , to store longer , process in a water bath'", "'bing cherries', 'raspberries', 'granulated sugar', 'fresh lemon juice'", "4", "https://www.food.com/recipe/653");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("657", "dinner rolls", "these seem long, but it is very precise and easy to follow.  ", "285", "'weeknight', 'time-to-make', 'course', 'preparation', 'healthy', 'breads', 'oven', 'rolls-biscuits', 'dietary', 'yeast', 'equipment', 'number-of-servings'", "175.2, 6.0, 6.0, 12.0, 10.0, 12.0, 9.0", "37", "'for the glaze:- 1 egg yolk 1 tbsp water put the milk into a saucepan and bring just to the boil', 'pour 4 tbsp of the milk into a small bowl and let cool to lukewarm', 'meanwhile cut the butter into pieces', 'add the butter and sugar to the remaining milk in the pan , stirring occasionally , until butter is melted', 'let cool to lukewarm', 'sprinkle the yeast over the 4 tbsp milk and let stand , stirring once , until dissolved , about 5 minutes', 'in a large bowl , beat eggs just until mixed', 'add the cooled sweetened milk , salt and dissolved yeast', 'stir in half of the flour and mix well with your hand', 'add the remaining flour , 2 ozs at a time , mixing well after each additions', 'keep adding flour until the dough pulls away from the sides of the bowl in a ball', 'it should be soft and slightly sticky', 'turn the dough onto a floured work surface', 'sprinkle the dough and your hands with flour , and begin to knead by holding the dough with one hand and pushing it away from you with the other', 'continue to knead by peeling the dough away from the surface', 'give the dough a quarter turn , and knead until it is very smooth , elastic and forms a ball , 5 - 7 minutes', 'if the dough sticks while kneading , flour the work surface', 'wash the large bowl and brush it with melted butter', 'put the kneaded dough in the bowl , and flip it so the surface is lightly buttered', 'cover the bowl with a damp tea towel and let the dough rise in a warm place until doubled in bulk , about 1 - 1 1 / 2 hours', 'to shape and bake the rolls:- brush 2 baking sheets with melted butter', 'turn the dough onto a lightly floured surface and knead with your hand just to knock out the air , 15 - 20 seconds', 'cover the dough , and let it rest , about 5 minutes', 'cut the dough in half', 'with your hands , roll 1 piece of the dough into a cylinder about 2 inches in diameter', 'cut the cylinder into 8 equal pieces', 'repeat to divide the remaining dough', 'to shape round rolls , cup a piece of dough under the palm of your hand , and roll the dough in a circular motion so it forms a smooth ball', 'arrange 8 rolls on each baking sheet', 'cover with a dry tea towel and let rise in a warm place until doubled in bulk , about 30 minutes', 'heat the oven to 425 f', 'make the glaze by beating the egg yolk with the water until frothy', 'brush the rolls with glaze , then bake them in the oven until golden brown , 15 - 18 minutes', 'turn the rolls over and tap the bottoms with your knuckles', 'they should sound hollow when tapped', 'cheers , doreen doreen randal , wanganui', 'new zealand'", "'milk', 'unsalted butter', 'sugar', 'dried yeast', 'eggs', 'salt', 'white flour'", "7", "https://www.food.com/recipe/657");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("658", "dried fruit roll ups", "fruit roll-ups", "1495", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'low-protein', '5-ingredients-or-less', 'very-low-carbs', 'lunch', 'snacks', 'fruit', '1-day-or-more', 'easy', 'low-fat', 'dietary', 'low-sodium', 'gluten-free', 'low-cholesterol', 'low-saturated-fat', 'low-calorie', 'low-carb', 'free-of-something', 'low-in-something', 'number-of-servings'", "215.2, 1.0, 200.0, 0.0, 4.0, 0.0, 18.0", "15", "'for each cup of fruit you will need 1-1 / 2 tablespoons of sugar', 'chop peaches , nectarines or plums very fine , being careful to reserve the juice', 'place fruit and its juices in saucepan and bring to a boil', 'boil until candy thermometer reads 180 degrees', 'cool slightly', 'put into blender and mix until smooth', 'return to heat and cook until the mixture has the consistency of a thick paste and is reduced by half', 'spread fruit 1 / 4-inch thick on plastic-wrapped trays or cookie sheets', 'cover pans with a cheesecloth screen , taking care to keep the cheesecloth from touching fruit', 'place in direct sunlight for 12 to 24 hours until dry', 'you can also bake in a 250-degree oven for about 4 hours', 'put the fruit on a lightly greased cookie sheet or use foil to line tray', 'fruit rollups are done when edges pull back from plastic and center is not sticky when cooled', 'ten cups of fruit will cover a 12 x 17-inch tray', '- - - - - - - - - - - - - - - - - - serving ideas : make dried fruit roll-ups the old-fashioned way'", "'peaches', 'nectarines', 'plums', 'sugar'", "4", "https://www.food.com/recipe/658");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("659", "drunken chicken marinade", "hi, it's been a very long time since the last time i posted to r.f.r, but i couldn't resist sending this when i saw the drunken chicken thread, this is a relatively new favorite of mine i created - i like it best for hickory or mesquite grilled chicken - but i think it works well for beef too...  ", "115", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', '5-ingredients-or-less', 'condiments-etc', 'easy', 'marinades-and-rubs', 'dietary', 'number-of-servings', '4-hours-or-less'", "222.0, 7.0, 8.0, 198.0, 30.0, 12.0, 14.0", "6", "'optional add-ins: garlic - i use powdered for the intensity- but crushed cloves of garlic will also be good', 'splash of cayenne pepper sauce rotel chiles and tomatoes a-1 sweet and tangy sauce sriracha chili sauce substitution: vodka or honey mead for cider', '---- combine all ingredients and pour over meat to be marinated', 'the estimated amount above marinates about 2 1 / 2 to 3 lbs of chicken', 'marinate in tightly sealed dish at minimum for about 1 1 / 2 hours i marinated overnight and had wonderful results', 'i hope you enjoy it'", "'sage', 'coarse black pepper', 'hard apple cider', 'soy sauce'", "4", "https://www.food.com/recipe/659");
