INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("661", "aunt kay s pork chops casserole", "aunt kay's pork chops casserole", "25", "'30-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'casseroles', 'main-dish', 'pork', 'oven', 'dietary', 'low-sodium', 'gluten-free', 'free-of-something', 'low-in-something', 'meat', 'equipment'", "877.3, 46.0, 33.0, 11.0, 110.0, 42.0, 31.0", "12", "'line a 9"" x 13"" pan with cooking foil , shiny side in', 'spray with cooking spray lightly', 'place 4 to 6 medium sized chops inside', 'season with salt and pepper', 'top in 3rds: make 2 cups instant rice to fill one-third , center third is a can of drained baby green peas , last third place 4 medium peeled and cut-up potatoes', 'chop one small onion over all', 'dollop margarine in small chunks over top', 'season with salt and pepper again', 'roll up excess foil to seal casserole on top and ends', 'bake at 350 degrees fahrenheit for 1-1 / 2 hours', 'yummy casserole !', 'instead of the green peas i have used green beans or corn before to change the flavor on the chops , but i like the peas best !'", "'pork chops', 'salt and pepper', 'instant rice', 'baby green peas', 'potatoes', 'onion', 'margarine'", "7", "https://www.food.com/recipe/661");
