INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("666", "croissant bread pudding with brandy cream sauce", "my good friend sharon stein shared this recipe with me a while back.  i  knew by looking at it how wonderful it would be.   the original recipe called for a  cornstarch-thickened caramel  sauce, but i usually change it to reflect a true  caramel sauce, as well as substituting blueberries for the raisins...exquisite!  ", "85", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'desserts', 'oven', 'puddings-and-mousses', 'dietary', 'equipment', '4-hours-or-less'", "648.9, 55.0, 177.0, 24.0, 19.0, 105.0, 24.0", "15", "'brandy cream sauce: 2 ounces butter 1 cup brown sugar 1 / 2 cup water 1 / 2 cup cream 1 teaspoon vanilla 1 tablespoon cornstarch , mixed to paste with a little water 2-3 tablespoons brandy preheat oven to 350f', 'grease a 9 x 11"" casserole dish', 'place half the croissants in the dish', 'sprinkle with raisins , and then top with remaining croissants', 'beat the eggs till light , gradually adding the sugar until thick', 'whisk in milk , cream , brandy , melted butter , cinnamon , nutmeg , vanilla and salt', 'pour mixture over the croissants , cover , and leave to absorb in the refrigerator for an hour or so', 'before baking , bring to room temperature', 'dot with butter and sprinkle with cinnamon / sugar', 'bake 40 minutes', 'serve immediately with sauce', 'sauce: in a small saucepan , combine butter , brown sugar and water and stir until boiling and sugar has dissolved', 'add cream , vanilla , and cornstarch paste and stir until thickened , lastly , add brandy', 'sauce can be made in advance and reheated just before serving', 'the nice thing about this is that you can do everything in advance , and bake it while you are eating the main course'", "'plain croissants', 'raisins', 'eggs', 'sugar', 'milk', 'cream', 'brandy', 'butter', 'cinnamon', 