INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("689", "crunchy oven fried fish", "", "57", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'main-dish', 'seafood', 'oven', 'fish', 'dietary', 'equipment'", "206.6, 2.0, 2.0, 6.0, 58.0, 1.0, 5.0", "14", "'preheat oven to 450', 'cut fish into serving size pieces', 'mix flour , lemon pepper , and 1 / 4 tsp salt in a shallow dish', 'beat egg white until frothy', 'place in another shallow dish', 'combine bread crumbs , cornmeal , lemon peel , and basil in a third shallow dish', 'dip top of fillets into flour mixture', 'shake off any excess', 'dip tops into egg white', 'coat with crumb mixture', 'spray a shallow baking pan with nonstick cooking spray', 'place fillets in pan , coating side up', 'tuck under thin edges', 'bake for 6 to 12 minutes or until fish flakes easily with a fork'", "'fish fillet', 'all-purpose flour', 'lemon pepper', 'egg white', 'fine dry breadcrumb', 'cornmeal', 'lemons, rind of', 'dried basil', 'nonstick cooking spray'", "9", "https://www.food.com/recipe/689");
