INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("691", "cream of chicken soup with wild rice", "good friend nancy dooley shared this with me, and it's become a family  favorite...especially good for fall and winter, but we eat it year round.", "150", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'bisques-cream-soups', 'soups-stews', 'poultry', 'rice', 'american', 'fall', 'winter', 'chicken', 'dietary', 'seasonal', 'midwestern', 'meat', 'whole-chicken', 'pasta-rice-and-grains', '4-hours-or-less'", "610.0, 48.0, 11.0, 46.0, 70.0, 69.0, 14.0", "26", "'i used superior touch chicken base and it was heavenly', 'cook wild rice according to package directions for 30 minutes', 'drain off liquid and rinse thoroughly', 'set partially cooked rice aside', 'in a large saucepan , combine the chicken and water', 'bring to boiling , reduce heat , and simmer for 40 minutes or until the chicken is tender', 'remove chicken from broth and let stand until cook enough to handle', 'skim fat from broth', 'strain and reserve broth', 'remove chicken meat from bones', 'cut into bite-size pieces', 'in the same saucepan , cook celery and onion in hot oil for 4-5 minutes', 'add mushrooms and cover and cook for 5 to 10 minutes or until everything is tender , stirring now and then', 'remove from heat', 'return broth to the saucepan', 'add the partially cooked wild rice to the chicken broth mixture', 'stir in the bouillon granules and white pepper', 'bring to boiling', 'reduce heat and simmer , uncovered , for 15 minutes', 'in a large separate saucepan , melt the butter', 'stir in flour until it all clings together and is smooth', ""add the milk all at once and stir and cook until it's bubbly and thick"", 'add some hot broth mixture to the white sauce mixture and stir until smooth', 'return all to the broth mixture', 'stir in the chicken pieces and