INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("696", "creme brulee", "", "235", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'desserts', 'eggs-dairy', 'french', 'oven', 'european', 'dinner-party', 'dietary', 'gluten-free', 'free-of-something', 'equipment', '4-hours-or-less'", "573.3, 74.0, 105.0, 3.0, 13.0, 144.0, 9.0", "52", "'preheat oven to 300f', 'in top of double boiler over hot water , heat cream , vanilla bean and sugar to almost boiling', 'lower heat and simmer for 1 minute', 'remove from heat', 'beat eggs and egg yolks', 'pour cream mixture in a thin stream into eggs , stirring constantly', 'return to double boiler', 'cook over medium heat , stirring with a wooden spoon , for 3 to 4 minutes or until custard coats back of spoon', 'remove vanilla bean', 'pour into 6 custard dishes or 4 to 5 cup flame proof serving dish', 'set dishes in a large pan of hot water on middle rack of oven', 'bake for 35 to 45 minutes , or until center of custard is set', 'remove custard from water and cool', 'cover and chill', 'to serve , sift brown sugar on top of creme', 'place dish into bowl of crushed ice', 'leaving door open , place under broiler at least 6"" from flame until hard crust of caramelized sugar is formed', 'serve immediately or chill until serving time', 'pass bowl of fresh raspberries along with creme for the perfect topping', 'add a spoonful of whipped creme to the custard , if desired', 'variations , flavored creme brulee:', 'vanilla bean:', 'add 1 pod for 3 cups of cream', 'break it open lengthwise or simply pierce it , and put it into liquid before it boils', 'chocolate:', 'add 4 1 / 2 oz cooking chocolate for each 3 cups of cream , melted in warm liquid', 'praline:', 'add 4 1 / 2oz pralines to each 3 cups of cream after it is cooked', 'nuts:', 'add 1 cup finely chopped pecans or brazil nuts for 3 cups cream after it is cooled', 'coffee:', 'use 3 oz coffee beans finely ground , then grilled in a pan of 3 cups cream', 'add to boiling liquid', 'infuse for 15 minutes , then strain', 'bananas:', 'mash 2 soft bananas', 'saute in 1 tbsp butter', 'add 2 tbsp brandy and 2 tsp brown sugar', 'cook for 5 minutes', 'pat down on bottom of creme brulee pan before adding liquid', 'use a double boiler to scald the cream unless you have a heavy saucepan', 'use very low heat', 'milk scorches easily and , when scorched , should be thrown out', 'beat eggs or egg yolks with a wire whisk or beater', 'add sugar gradually and continue whipping until it is thoroughly mixed inches', 'never add eggs directly to a hot mixture', 'pour some of the hot liquid into the eggs while stirring briskly , then return the warmed combination to the remaining hot liquid in the pan', 'stir the custard constantly over low heat until it acquires body', 'test it by letting it run off the back of a spoon', 'if a light coating clings to the spoon , the custard is finished', 'stir the custard occasionally as it cools to prevent a surface skin from forming', 'when it is cool , chill it , covered lightly with a clean dish towel , in the refrigerator'", "'heavy cream', 'vanilla bean', 'sugar', 'eggs', 'egg yolks', 'dark brown sugar', 'fresh raspberry', 'baking chocolate', 'pralines', 'pecans', 'coffee beans', 'bananas', 'butter', 'brandy', 'brown sugar'", "15", "https://www.food.com/recipe/696");
