INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("742", "yolkless noodles", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'healthy', 'side-dishes', 'pasta', 'low-fat', 'dietary', 'low-cholesterol', 'low-saturated-fat', 'healthy-2', 'low-in-something', 'pasta-rice-and-grains'", "138.2, 2.0, 0.0, 6.0, 8.0, 1.0, 8.0", "25", "'in a large mixing bowl stir together the 2 cups flour and salt', 'make a well in the center of the dry mixture', 'in a small mixing bowl combine egg white , water , and oil', 'add to the dry mixture', 'mix well', 'sprinkle kneading surface with 1 tbsp of the remaining flour', 'turn dough out onto floured surface', 'knead till dough is smooth and elastic , adding 1 to 2 tbsp additional flour as needed', 'cover and let the dough rest for 10 minutes', 'divide dough into 4 equal portions', 'on a lightly floured surface , roll each portion into a 12"" square ,', 'let stand , uncovered , about 20 minutes', 'loosely roll up dough jelly-roll style', 'cut into 1 / 4"" wide strips', 'shake the strands to separate and cut into 2 to 3"" lengths', 'if using a pasta machine , pass each portion through machine according to manufacturer\'s directions till dough is 1 / 16"" thick', 'cut into 1 / 4"" wide strips', 'cut strips into 2 to 3"" lengths', 'to store cut noodles , spread them on a wire cooling rack', 'let noodles dry overnight or till completely dry', 'place in an airtight container and refrigerate for up to 3 days', 'or , dry the noodles for at least 1 hour and seal in a freezer bag or freezer container', 'freeze up to 8 months', 'cook noodles for 1 1 / 2 to 2 minutes or till tender but still firm , allowing a few more minutes for dried or frozen noodles', 'drain noodles well'", "'all-purpose flour', 'salt', 'egg whites', 'water', 'olive oil'", "5", "https://www.food.com/recipe/742");
