INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("749", "zucchini lasagna  lasagne    low carb", "great for anyone going low carb or who has so many zucchini, they don't know what to do!! :) we found the sauce to be very mild. i think it would be better with twice as much garlic. i boiled my zucchini slices for about 3 minutes so they were partially cooked. i wanted to make sure they were soft in the finished dish or i knew the kids wouldn't eat them. ;) the cottage cheese provides a slightly tangy note. i think we would have preferred ricotta, but that is personal taste. i use my 11x7 baking dish and got 6 pieces the size of what you see in my photos. both of the guys ate two so it fed 4 of us, and i double the meat. adjust the recipe accordingly for your family.", "60", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'main-dish', 'beef', 'pasta', 'vegetables', 'oven', 'european', 'italian', 'lasagna', 'dietary', 'low-calorie', 'low-carb', 'ground-beef', 'low-in-something', 'meat', 'pasta-rice-and-grains', 'squash', 'equipment'", "261.1, 17.0, 42.0, 26.0, 48.0, 25.0, 5.0", "15", "'cook zucchini until tender , drain and set aside', 'fry meat and onions until meat is brown and onions are tender', 'drain fat', 'add next 8 ingredients and bring to a boil', 'reduce heat', 'simmer , uncovered 10 minutes or until reduced to 2 cups', 'in small bowl slightly beat egg', 'add cottage cheese , half of shredded cheese and flour', 'in baking-roasting pan arrange half of the meat mixture', 'top with half of the zucchini and all the cottage cheese mixture', 'top with remaining meat and zucchini', 'bake uncovered at 375 degrees f for 30 minutes', 'sprinkle with remaining cheese', 'bake 10 minutes longer', 'let stand 10 minutes before serving'", "'zucchini', 'lean ground beef', 'onion', 'tomatoes', 'tomato paste', 'garlic clove', 'dried oregano', 'dried b