INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("868", "lentil bulghur salad", "side dish or main dish salad", "50", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'salads', 'beans', 'dietary', 'lentils', 'number-of-servings'", "357.9, 34.0, 10.0, 17.0, 23.0, 13.0, 10.0", "22", "'place lentils in a sieve , and rinse thoroughly', 'transfer lentils to sauce pan with 2 1 / 2 cups water and bring to a boil', 'reduce heat to low and simmer for 30-45 minutes , until lentils are tender but not mushy', 'lentils should hold their shape', 'while lentils are simmering , place the bulghur wheat in a bowl', 'boil the remaining cup of water and pour over the bulghur wheat', 'cover and let stand until all water has been absorbed , about 15 minutes', 'fluff bulghur wheat with a fork', 'when the lentils are done , pour off any remaining liquid and let the lentils cool', 'heat an iron-cast skillet over high heat', 'add walnut pieces and stir them constantly with a wooden spoon until they begin to brown', 'turn off the heat and set aside', 'in a small bowl , mix olive oil , lemon juice , vinegar , garlic , and salt and pepper with a fork to make a dressing', 'place lentils in a large bowl , preferably glass', 'with a wooden spoon , carefully mix in the bulghur wheat , followed by the chopped vegetables and the toasted walnuts', 'pour dressing evenly over the salad , and mix in with a fork', 'refrigerate salad at least one hour to allow the flavors to blend', 'suggestions french lentils are small , dark-green lentils that can be found in most health food stores', 'if they are unavailable , substitute brown lentils', 'bulghur wheat can also be found in health food stores and ethnic groceries', 'it comes in several different degrees of coarseness', 'for this recipe , the finely cracked variety is preferred'", "'french lentils', 'water', 'bulgar wheat', 'walnut pieces',