INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("992", "jalapeno pepper poppers", "originally from ""taste of home"" magazine", "30", "'30-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'for-large-groups', 'appetizers', 'vegetables', 'dietary', 'low-carb', 'low-in-something', 'number-of-servings'", "111.4, 14.0, 3.0, 7.0, 8.0, 24.0, 1.0", "11", "'in a mixing bowl , combine cheeses , bacon and seasonings', 'mix well', 'spoon about 2 tbs', 'into each pepper half', 'roll in bread crumbs', 'place in a greased 15x10x1"" baking pan', 'bake , uncovered , 300 degrees for 20 minute for spicy flavor , 30 minute for med', 'and 40 minute for mild', 'serve with sour cream or dressing', 'avoid touching your face', 'i omit the bacon in this recipe'", "'cream cheese', 'sharp cheddar cheese', 'monterey jack cheese', 'bacon', 'salt', 'chili powder', 'garlic powder', 'fresh jalapeno', 'dry breadcrumbs', 'sour cream'", "10", "https://www.food.com/recipe/992");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("1005", "pizza dough and sauce", "i make the dough with 1/2 white and 1/2 wheat flour. it gives the crust more substance and a better flavor. even my kids and their cousins prefer it this way. i like using olive oil in both the crust and the sauce. since we only eat one pizza for a meal, i have included two methods for freezing the second one, or you can freeze both for oamc. note: prep time does not include rise time.", "40", "'60-minutes-or-less', 'time-to-make', 'course', 'preparation', 'for-large-groups', 'healthy', 'flat-shapes', 'breads', 'main-dish', 'condiments-etc', 'oven', 'easy', 'pizza', 'freezer', 'dietary', 'low-cholesterol', 'low-saturated-fat', 'oamc-freezer-make-ahead', 'low-in-something', 'equipment', 'number-of-servings'", "165.1, 5.0, 8.0, 18.0, 8.0, 2.0, 9.0", "19", "'in a large bowl , sprinkle yeast over warm water and allow to set until it begins to foam , about 5 minutes', 'stir in salt , oil and half of flour', 'gradually add remaining flour , mixing well', 'knead 8-10 minutes or until smooth and elastic', 'place in greased bowl and let rise until double punch down and let rise again until double', 'punch down and divide', 'pat out on two pizza pans', 'top with pizza sauce & toppings', 'bake at 400 degrees for 20-25 minutes', 'pizza sauce: mix all ingredients together , blending well', 'makes enough for two 12- or14-inch pizzas or one thick 16-inch pizza', 'top with meats , cheese and other toppings', 'to freeze , prepared pizza method: double wrap and freeze the prepared pizza on the pizza pan', 'unwrap and bake the frozen pizza at 400f for about 22 minutes', 'alternative freezing method: after first punch down of dough , divide dough and place in a zip-loc bag and place in the freezer', 'to prepare , remove from freezer and place in a greased bowl', 'the dough will take about 8-10 hours to thaw and raise slightly', 'pat out on grease pizza pan and proceed as directed above', 'i also freeze half the sauce in a small container for future use'", "'yeast', 'warm water', 'salt', 'oil', 'flour', 'tomato paste', 'red wine', 'oregano', 'sugar', 'vegetable oil', 'parmesan cheese'", "11", "https://www.food.com/recipe/1005");
