INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("1024", "potato plum dumplings", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'for-large-groups', 'low-protein', 'healthy', 'side-dishes', 'potatoes', 'vegetables', 'hungarian', 'european', 'dietary', 'low-sodium', 'low-in-something', 'number-of-servings'", "270.6, 13.0, 40.0, 9.0, 11.0, 25.0, 14.0", "38", "'scrub potatoes well but do not peel', 'in a saucepan combine potatoes and water to cover and bring to a boil', 'cook until potatoes are tender when pierced with a fork', 'drain', 'when potatoes are cool enough to handle , peel and , while still warm , press through a potato ricer into a large mixing bowl', 'let cool', 'add salt , eggs , and the 1 tablespoon butter to potatoes and mix well', 'gradually mix in the 1 cup flour and farina until dough is easy to work , adding more flour if it is too wet', 'turn dough out onto a floured board and knead well until smooth and fairly soft', 'with your hands form dough into a large sausage-shaped roll', 'cut roll into 12 to 16 equal pieces', 'cut open plums and remove pits', 'replace each pit with a sugar cube and re-form plum', 'with a floured rolling pin , roll out each dough piece into a 3-inch square 1 / 4 to 1 / 3 inch thick', 'place 1 plum in center of each square', 'bring up corners of square to enclose plum completely', 'no part of plum must be exposed', ""pinch dough edges together firmly so that dumpling doesn't break open during cooking"", 'meanwhile , fill a large saucepan with water and bring to a boil over high heat', 'drop in dumplings , being careful not to crowd pan , and boil gently , stirring occasionally', 'do not boil too hard or dumplings may split open', 'once dumplings rise to surface , cook until tender', 'with a slotted utensil carefully remove dumplings one at a time to a heated bowl', 'cover tightly to keep