INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("1045", "raisin cream pie", "when i first married (1950) my late husband wanted a cream pie with raisins. my grandmother told me how.  like my grandmother use to make - good! be sure to try this. this one will surprise you.    raisin cream pie  baked 9"" pie shell", "45", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'low-protein', 'healthy', 'pies-and-tarts', 'desserts', 'fruit', 'oven', 'low-fat', 'pies', 'dietary', 'low-sodium', 'low-in-something', 'equipment', 'number-of-servings'", "282.1, 7.0, 202.0, 6.0, 9.0, 12.0, 19.0", "24", "'add raisins to water in saucepan and simmer 5 minutes', 'beat egg yolks , sugar , vinegar and flour until light and creamy', 'slowly add to raisins', 'cook , stirring constantly , until filling is thick , 4 to 5 minutes', 'remove from heat , stir in butter and cool until lukewarm', 'pour raisin mixture into pie shell and top with meringue', 'perfect meringue', 'have egg whites at room temperature to obtain greatest volume', 'place in medium bowl with cream of tartar , salt and vanilla', 'beat with electric or hand beater , at medium speed , until entire mixture is frothy', 'do not beat until eggs stiffen', 'add sugar , a little at a time , beating well after each addition', 'do not under beat', 'beat until sugar dissolves to help prevent beading', 'to test , rub some of meringue between your fingers to see if it is still grainy', 'continue to beat until stiff , pointed peaks for when beater is slowly lifted', 'place spoonfuls of meringue around edge of pie filling , spreading it so it touches inter edge of crust to seal all around', 'this prevents shrinkage', 'pile remainder of meringue in center of pie and spread to meet meringue around the edge', 'if filling is not covered completely , the oven heat may cause it to weep', 'lift up meringue over pie in points with back of teaspoon', 'bake in moderate oven 12 to 15 minutes , or until meringue peaks are golden brown', 'too long baking may cause weeping', 'cool gradually away from drafts'", "'raisins', 'water', 'eggs', 'sugar', 'vinegar', 'flour', 'butter', 'egg whites', 'cream of tartar', 'salt', 'vanilla'", "11", "https://www.food.com/recipe/1045");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("1048", "sour cream raisin pie", "this pie was made by my grandmother and the recipe was passed to me by my mother.  this pie is delicious and always brings back fond memories when i make it. ", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'north-american', 'low-protein', 'pies-and-tarts', 'desserts', 'fruit', 'american', 'oven', 'pies', 'dietary', 'low-in-something', 'equipment'", "662.7, 41.0, 286.0, 18.0, 11.0, 56.0, 34.0", "5", "'cook all filling ingredients in sauce pan on top of stove until mixture thickens and foam disappears', 'use a large pan for this as the vinegar and soda will foam up', 'turn into unbaked pie crust and top with second crust', 'brush top with milk and cut slits to vent', 'bake at 425 degrees for 20 min and then reduce oven temp to 350 degrees and bake for another 20 to 30 minutes or until brown'", "'sugar', 'flour', 'cinnamon', 'allspice', 'clove', 'sour cream', 'raisins', 'vinegar', 'baking soda', 'salt', 'pastry for a double-crust 9-inch pie'", "11", "https://www.food.com/recipe/1048");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("1069", "rock candy for kids", "", "30", "'30-minutes-or-less', 'time-to-make', 'course', 'cuisine', 'preparation', 'occasion', 'north-american', 'low-protein', 'healthy', '5-ingredients-or-less', 'desserts', 'american', 'southern-united-states', 'easy', 'kid-friendly', 'low-fat', 'candy', 'dietary', 'low-sodium', 'gluten-free', 'low-cholesterol', 'low-saturated-fat', 'free-of-something', 'low-in-something', 'number-of-servings'", "1548.0, 0.0, 1596.0, 0.0, 0.0, 0.0, 133.0", "10", "'put one cup of water into a small saucepan , pour 2 cups of granulated sugar into the water , heat the water and sugar on the stove over a medium heat , continue stirring until the sugar melts , keep adding sugar and stirring until it melts , stop adding sugar when you see that it will no longer dissolve in the water', 'remove the pot from the stove , let the liquid cool until it is just warm', 'pour the liquid into a clean glass jar', 'tie one end of a piece of heavy cord around the middle of a pencil', 'place the pencil over the top of the glass jar letting the cord fall into the liquid', 'crystals will begin to form in a few hours the next day , remove the cord from the jar , pour the sugar liquid back into the saucepan , reheat and cool it just as you did before', 'pour the liquid back into the jar and reinsert the cord with the crystals into it', 'more crystals will form', 'if you repeat this procedure every day the crystal candy will grow bigger and bigger', 'when it has reached a size that pleases you , snip off any excess string and enjoy'", "'granulated sugar', 'water'", "2", "https://www.food.com/recipe/1069");
