INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("1564", "linzer bar", "i adopted this recipe from recipezaar in august 2006.  i haven't tried the recipe, but i hope to very soon.  here is what the original poster said about this recipe: ""you can take one of two approaches for the top crust here - a lattice top is more traditional and a little more work - the dough can be a little crumbly so you need to add a little water to roll it out.  you can skip the lattice and simply crumble the top crust dough over the jam and bake.  it will look more like a cobbler.  either way will taste just fine!""", "60", "'60-minutes-or-less', 'time-to-make', 'course', 'preparation', 'desserts', 'oven', 'cookies-and-brownies', 'bar-cookies', 'dietary', 'equipment', 'number-of-servings'", "235.4, 12.0, 67.0, 8.0, 5.0, 25.0, 12.0", "16", "'in a large bowl stir together flour , sugar , brown sugar , baking powder , salt and cinnamon', 'with a pastry blender or two knives use scissor style , cut in butter or margarine until coarse crumbs form', 'with a fork stir in corn syrup and egg just until mixed', 'reserve 1 / 2 cup batter', 'press remaining batter into a 9x9x2-inch baking pan', 'spread with jam', 'stir 2 tablespoons flour into the reserved batter', 'if preparing a lattice add a couple tablespoons of water - one at a time - to make the dough easy to roll out', 'if you are dropping the dough by spoonfulls then just evenly scatter the top ""crust"" over the jam for a ""spotty"" look and bake', 'on a floured surface roll out to 1 / 4-inch thickness', 'cut into 1 / 4-inch wide strips', 'lattice strips over jam', 'bake in 375-degree oven 30 minutes , or until set', 'cool', 'cut into bars', 'serving size is really up to you - cut into as many bars as you want'", "'unsifted flour', 'sugar', 'brown sugar', 'baking powder', 'salt', 'cinnamon', 'butter', 'dark corn syrup', 'egg', 'raspberry jam', 'flour'", "11"