INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("2526", "warr shu gai almond boneless chicken", "warr shu gai or almond boneless chicken, like chop suey, is a chinese-inspired american dish.  so far as i have been able to determine, it originated in detroit. it was a favorite dish of my childhood there.  every cantonese restaurant in the area serves it, but i've never been able to find it outside of michigan.  descriptions of it to chinese restaurant personnel elsewhere have been met with blank stares.  ", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'poultry', 'easy', 'chicken', 'dietary', 'low-calorie', 'low-carb', 'low-in-something', 'meat', 'chicken-breasts', 'number-of-servings'", "341.9, 21.0, 3.0, 68.0, 63.0, 34.0, 6.0", "18", "'prepare sauce: in a small saucepan , stir together cornstarch and water until smooth', 'gradually stir in chicken broth , mushrooms , butter , soy sauce and bouillon granules', 'bring the mixture to a boil , stirring constantly', 'let boil 1 minute', 'keep warm', 'prepare batter: beat together cornstarch , flour , baking powder , egg and water until smooth', 'coat each piece of chicken with batter', 'pour vegetable oil into a large skillet or wok to the depth of 1 / 2 inch', 'heat to 375 degrees', 'cook coated chicken pieces in oil , turning once , until golden -- 5 to 7 minutes', 'drain on pa per towels', 'cut chicken diagonally into strips', 'reassemble strips in chicken breast shapes and place on a bed of shredded lettuce', 'sprinkle with almonds and green onion', 'spoon sauce over chicken and serve immediately', 'makes four to six servings', ""it's been some time since i last made this , but i recall that the batter seemed not quite the same , but the rest is authentic"", 'this can also be made with duck , in which case it becomes warr shu opp'", "'skinless chicken breasts', 'salt', 'dry sherry', 'cornstarch', 'water', 'chicken broth', 'mushrooms', 'butter', 'soy sauce', 'chicken bouillon granules', 'flour', 'baking powder', 'egg', 'vegetable oil'", "14", "https://www.food.com/recipe/2526");
