INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("2582", "tujague s pecan pie", "when the french settled in new orleans they discovered the pecan. and they  promptly made good use of it by creating pecan brittle, pecan pralines, pecan  sauce and of course pecan pie. the first pecans i ever ate came from my  grandmother's pecan tree in her backyard. every christmas she would send a 50  pound gunny sack of pecans to my mother. my grandmother's pecans were not those  tasteless thin shelled kind; hers had thick shells with just as much shell as  meat and 10 times more taste.    this recipe for pecan pie is from my mother's uncle whose family owned tujague's  restaurant in new orleans for more than 70 years.", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'pies-and-tarts', 'desserts', 'fruit', 'american', 'southern-united-states', 'oven', 'holiday-event', 'pies', 'nuts', 'dietary', 'equipment'", "585.4, 47.0, 180.0, 12.0, 12.0, 34.0, 25.0", "5", "'preheat the oven to 400 degrees', 'in a glass mixing bowl beat the eggs , blending in the sugar , syrup , butter , vanilla , salt , armagnac and pecans', 'pour the mixture into the pie shell and bake 10 minutes', 'lower the oven to 350 degrees and bake 30 minutes or until a wooden skewer or knife inserted into the pie comes out clean', 'cool on a wire rack'", "'9 inch pie shell', 'eggs', 'sugar', 'dark karo syrup', 'butter', 'vanilla extract', 'salt', 'armagnac', 'pecan halves'", "9", "https://www.food.com/recipe/2582");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("2583", "the camellia grill s pecan pie", "the camellia grill's pecan pie is justifiably famous; oodles of 'em are eaten in-house at this beloved and venerable uptown eatery, and they say they ship pies all over the country. they used to hand out a little card with the recipe, but they don't anymore. here's what was on the card!", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'pies-and-tarts', 'desserts', 'fruit', 'oven', 'pies', 'nuts', 'dietary', 'equipment', 'number-of-servings'", "384.9, 26.0, 128.0, 8.0, 7.0, 23.0, 19.0", "6", "'beat eggs with a wire whisk or fork', 'add salt , butter , syrup , sugar and vanilla', 'mix well', 'pour into pastry shell', 'sprinkle with pecans', 'bake at 350f for 45-50 minutes'", "'eggs', 'salt', 'butter', 'light corn syrup', 'brown sugar', 'vanilla extract', 'unbaked 9-inch pie shell', 'pecans'", "8", "https://www.food.com/recipe/2583");
