INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("2639", "sweet chicken curry", "this is an adaptation of my mother's curry recipe.  it produces a delightfully mild (depending on your curry mix) and sweet stew.  this mixture makes enough mild, sweet curry to feed six unashamedfly hungry people, and freezes well.to lose the syrupy/sweet aspect, use plain coconut cream instead of the colada mix.  if you can't find coconut milk, use some plain yogurt and brown sugar or honey instead.", "35", "'60-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', 'for-large-groups', 'poultry', 'chicken', 'dietary', 'meat', 'chicken-breasts', 'chicken-thighs-legs', 'number-of-servings'", "114.2, 12.0, 1.0, 1.0, 17.0, 10.0, 0.0", "9", "'in a pot , brown the meat , onions and garlic in oil', 'generously sprinkle curry powder on meat', 'mix your stock and thickener in a cup', 'add stock mixture , coconut milk and cream to the chicken', ""if you don't want to break the bank over one meal , forage in your refrigerator for any questionable vegetables you want to use up and start dicing potatoes and throw them in now"", 'season to taste', 'simmer for at least 10 minutes- thickening your sauce to desired consistency , though feel free to torment your family and / or guests by cooking this for a few hours', 'serve with rice , thick pita bread , salad and your fave condiments', 'suggested condiments include corn kernels , peanuts , raisins , chopped hard-boiled eggs , sliced banana in lemon or lime juice , sliced cucumber in yoghurt , dried coconut , raisins and other dried fruits , and chutney'", "'olive oil', 'onions', 'garlic', 'curry powder', 'hot chili pepper', 'chicken stock', 'bisto', 'coconut cream pina colada mix', 'low-fat coconut milk', 'chicken'", "10", "https://www.food.com/recipe/2639");
