INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("2679", "nanna s yorkshire pudding", "these are my nanna's and mother's yorkshire pudding recipes. i guess the problem with traditional recipes is that they are seldom very accurate. this does not  produce a ""popover"" style yorkshire pudding like i have found in north american  restaurants. the result of the recipes below is the more traditional  style--slightly eggy, and ""fallen."" my siblings and i much prefer it this way.  batter resting time not included in preparation time.", "25", "'15-minutes-or-less', 'time-to-make', 'course', 'cuisine', 'preparation', '5-ingredients-or-less', 'side-dishes', 'oven', 'easy', 'european', 'english', 'dietary', 'low-sodium', 'low-in-something', 'equipment', 'number-of-servings'", "127.5, 12.0, 0.0, 1.0, 8.0, 19.0, 3.0", "14", "'in a bowl combine flour and salt', 'with a handheld mixer add the milk , in a stream , until smooth', 'add water and eggs and beat until combined well and bubbly', 'let stand , covered , at room temperature for 1 hour', 'preheat oven to 450f', 'divide drippings among six muffin pan cups', 'heat the cups in the oven until almost smoking', 'beat batter until bubbly and divide among muffin cups', 'bake 10 minutes in lower third of the oven without opening the oven door', 'reduce oven temperature to 350 degrees f and continue to bake for another 10 minutes until puffed , crisp and golden brown', 'serve immediately', ""mum's yorkshire pudding 1 cup flour 1 cup milk 2 eggs salt & pepper put fat from roast into glass pie plate or use vegetable oil"", 'mix listed ingredients together', 'cook in 450f oven for 25 minutes'", "'flour', 'milk', 'eggs', 'fat'", "4", "https://www.food.com/recipe/2679");
