INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("2691", "lamb chops with mandarins", "i have cheated making this and used canned mandarins, must admit though, if you have enough of the fruit it's nicer.  ", "45", "'60-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'lamb-sheep', 'dietary', 'meat'", "36.5, 0.0, 15.0, 0.0, 0.0, 0.0, 1.0", "17", "'trim the chops so the are neat and free from any excess fat', 'roll the tails up so the chops make a round , if necessary secure with a toothpick', 'in a large pan , heat just enough butter to cover the bottom of the pan and brown the chops on both sides', 'season lightly with salt and pepper', 'add the wine and mandarin juice', 'cover , lower heat and simmer until the meat is quite tender--about 30 mins', 'when it is cooked , lift the meat on to a serving plate and keep warm', 'mix the cornflour and water to a smooth paste and stir into the pan juices', 'cook quickly with regular stirring until they thicken slightly', 'add the mandarin segments to the sauce and cook about 1 minute', 'pour the sauce over the chops and garnish with parsley', 'serves 6', 'n', 'b', '---- to make mandarin juice , crush mandarin segments and strain the juice from them', 'cheers , doreen doreen randal , wanganui', 'new zealand'", "'middle loin chops', 'cornflour', 'butter', 'salt & pepper', 'water', 'white wine', 'mandarin juice', 'mandarin segment', 'watercress'", "9", "https://www.food.com/recipe/2691");
