INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("2698", "lemonade cake  3", "adopted recipe. i have made this once and it didn't turn out as it had been written so i have changed a few things. be sure to grease and flour your pan very well. i have changed the glazing step to hopefully prevent the cake from sticking in the pan. if your cake mix calls for more than 1/4 cup applesauce as the substitution for oil, by all means do that. the cake mix i used only called for 1/4 cup so that is how i rewrote this. i hope the tweaks fix the problems and that you like it!", "65", "'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'north-american', 'healthy', 'desserts', 'fruit', 'american', 'oven', 'cakes', 'dietary', 'equipment', 'number-of-servings', '4-hours-or-less'", "286.7, 12.0, 118.0, 16.0, 10.0, 7.0, 16.0", "13", "'preheat oven to 350 degrees', 'grease and flour a bundt pan', 'dissolve jello in hot water and set aside to cool', 'do not jell', 'in a large bowl , mix cake mix , eggs and applesauce', 'add cooled jello', 'beat until well mixed', 'turn into bundt pan', 'bake 40-45 minutes or until toothpick inserted comes out clean', 'meanwhile: mix lemonade concentrate and sugar and let set , optional', 'while cake is still warm , loosen it from edges of pan', 'when completely cooled , turn out onto serving plate', 'you can top with the lemonade concentrate glaze or cool whip or just dust with the powdered sugar'", "'lemon jell-o gelatin', 'water', 'lemon cake mix', 'eggs', 'applesauce', 'frozen lemonade concentrate', 'sugar', 'whipped topping', 'powdered sugar'", "9", "https://www.food.com/recipe/2698");
