INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("2960", "pickled apples", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'low-protein', 'healthy', 'canning', 'desserts', 'fruit', 'low-fat', 'dietary', 'low-sodium', 'low-cholesterol', 'low-saturated-fat', 'low-in-something', 'apples', 'technique'", "2379.6, 6.0, 2209.0, 1.0, 6.0, 4.0, 197.0", "7", "'take ripe , hard , sweet apples', 'peel evenly , and if the apples are perfect leave them whole , otherwise cut into quarters', 'to a peck of apples take about two quarts of vinegar and four pounds of sugar , half an ounce of mace , half an ounce of cloves and the same amount of allspice , all unground , one teaspoonful of mustard seed , a few pepper grains and a little salt', 'heat the vinegar and sugar together until it boils , skim well , put the spices into a thin muslin bag and add to the vinegar , then put in the apples', 'place over the fire , and stew slowly until the apples are soft', 'then take out the apples , let the vinegar boil down and pour over the fruit', 'cover and put away'", "'apples', 'vinegar', 'sugar', 'mace', 'whole cloves', 'whole allspice', 'mustard seeds', 'pepper', 'salt'", "9", "https://www.food.com/recipe/2960");
