INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("2963", "pickled watermelon rind", "--adopted recipe--       i used to make watermelon rind pickle, but my recipe is in the group of my recipes that is ""hiding"" from me.  i will try to figure out how much rind, but i am guessing it is the peeled rind of one melon, and have changed the directions to reflect this.  i have also added the step of soaking overnight in salt water.  amount of salt is approximate, but i never made any kind of pickle without any salt, as this recipe originally was written.  everything in this recipe is subject to change when i find my recipe.  i have simply made some changes to try to make the recipe a little clearer, because i don't want someone to waste time and ingredients on a recipe that really seemed to be missing some ingredients and some steps.  preparation time includes standing time.", "2190", "'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'for-large-groups', 'canning', 'fruit', '1-day-or-more', 'easy', 'dietary', 'melons', 'number-of-servings', 'technique'", "117.7, 0.0, 106.0, 23.0, 0.0, 0.0, 9.0", "10", "'peel the hard rind from the outside of the watermelon , and scrape off most of the pink from the inside', 'chop into 3 / 4-1 inch pieces', 'add salt to water and pour over watermelon rind in a large glasss or ceramic bowl', 'soak overnight in the refrigerator', 'in the morning , pour off the salt water and rinse well', 'add three pounds of brown sugar to the vinegar in a cooking pan', 'bruise the spices , tie in a muslin bag and boil with the vinegar for five minutes', 'then pour over the rind and let it stand twenty-four hours at room temperature', 'the next day , drain and save the liquid and , after heating it to a boil again , pour it over the rind again and let stand another twenty-four hours at room temperature', 'the last morning , bring to a full boil and boil all together for 10 minutes , then pour into sterilized jars and process according to the directions in a reputable canning guide'", "'watermelon rind', 'water', 'salt', 'apple cider vinegar', 'brown sugar', 'cinnamon', 'cloves'", "7", "https://www.food.com/recipe/2963");
