INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3045", "homemade baked ziti", "easy ziti", "90", "'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'casseroles', 'main-dish', 'pasta', 'oven', 'european', 'italian', 'dietary', 'pasta-rice-and-grains', 'equipment', 'number-of-servings', '4-hours-or-less'", "679.4, 50.0, 46.0, 63.0, 69.0, 64.0, 20.0", "32", "'preheat oven to 350f', 'to make the sauce: first , brown the ground beef and port', 'sausage in separate frying pans over medium heat', 'make sure the beef is in a large frying pan , and drain the fat', 'when the sausage is done , pat it dry with paper towels to remove the grease', 'when the beef is just about done , grind the pepper over the beef , probably about 1 tbsp worth', 'add about 1 / 2 to 1 tbsp soy sauce and mix it in to the beef', 'you can replace the soy sauce with salt , but it adds a nice zing to the flavor', 'at this point , i usually cut the onion into chunks and throw it into my cuisinart with the garlic , and shred them to bits , and toss them in', 'but sliced into small bits works well also', 'add the onion and garlic , and let cook down until the onion gets a little clear', 'i add a little oil here sometimes , depending on how much fat was in the meat', 'add the tomato paste , and mix it in', 'i do the paste first because it makes it faster and easier to get the consistency right when the sauce comes in', 'add the sauce and mix it well', 'add generous amounts of the oregano and italian seasoning', ""don't go overboard on the basil though"", 'add the bay leaves', 'let it simmer', 'mix it , and let it simmer longer', 'start the water boiling for the ziti', 'add some salt and oil to the water to make it boil quicker , and to help keep the zitis from sticking', 'throw the green pepper , sausage , and mushrooms into the sauce', 'next stir about 4 or 5 tbsp parmesan cheese in to the sauce', 'when the ziti is done , drain them and dump into a large rectangular pyrex', ""i don't remember the exact size , but i think its around 9x18"", 'you may have a touch too many zitis , but they are delicious plain , so eat any extras', 'once the parmesan has melted into the sauce and made it stringy , and the sauce has thickened , stir the sauce into the zitis', 'mix it all well', 'now , lay the sliced mozzarella over the ziti like shingles on a roof', 'sprinkle over some more parmesan , maybe a tablespoons', 'bake at 350f for 45 minutes to 1 hour'", "'ground beef', 'sausage', 'onion', 'green pepper', 'garlic cloves', 'mushroom', 'tomato paste', 'tomato sauce', 'oregano', 'basil', 'italian seasoning', 'bay leaves', 'fresh parmesan cheese', 'black pepper', 'soy sauce', 'chili flakes', 'ziti pasta', 'mozzarella cheese', 'salt', 'canola oil'", "20", "https://www.food.com/recipe/3045");
