INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3157", "turkey noodle soup", "with thanksgiving close at hand (early october in canada and late november in the united states) this simple recipe helps utilize the inevitable turkey leftovers. enjoy!", "105", "'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'low-protein', 'soups-stews', 'pasta', 'poultry', 'turkey', 'dietary', 'low-cholesterol', 'low-saturated-fat', 'low-calorie', 'low-carb', 'low-in-something', 'meat', 'pasta-rice-and-grains', '4-hours-or-less'", "148.5, 9.0, 13.0, 62.0, 8.0, 16.0, 6.0", "27", "'in an eight quart kettle or dutch oven place turkey bones , water -- down through to and including the bay leaf', 'heat to boiling', 'reduce heat , cover and simmer for 1 hour', 'remove the bones to a platter and let c ool', 'add the parsley through to green beans', 'heat to boiling', 'reduce heat and simmer for 10 minutes', 'meanwhile , scrape meat from carcass and return meat to soup pot', 'add liquid if required', 'heat to boiling', 'add noodles and cook uncovered for 10 minutes', 'melt butter in a small frying pan', 'stir in flour', 'cook over low heat , stirring constantly , until the flour browns', 'stir into boiling soup', 'when the soup returns to a boil', 'reduce heat and simmer for 5 minutes', 'serve hot in large bowls - as if there were any other kind ! - enjoy', 'i have used yellow beans and a mixture of both', 'once i wound up using frozen mixed vegetables - and it was still good', 'on two makings , chicken was substituted for the turkey - and it turned out well', 'hints on herbs and spices: to substitute dried herbs for fresh , use 1 / 3 tsp powdered or 1 / 2 tsp crushed for every tbsp of fresh chopped herbs', 'to test for dryness before packing put a few sprigs or leaves in a tightly covered jar and watch for condensation', 'store herbs in tightly covered jars , away from heat', 'add mint , oregano , basil and any other dried herbs to recipes at the end or near end of cooking time for a more pronounced flavour', 'if you are enlarging recipes , spice to taste rather than to measure', 'when preparing cooked foods for freezing , it is advisable to under-season them with herbs or spices , then add more seasoning to taste when you reheat them and you will have the ultimate in flavour'", "'water', 'celery', 'celery leaves', 'onion', 'chicken bouillon cubes', 'salt', 'black pepper', 'bay leaf', 'fresh parsley', 'fresh peas', 'carrot', 'cut green beans', 'fine egg noodles', 'butter', 'flour', 'turkey carcass'", "16", "https://www.food.com/recipe/3157");
