INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3159", "traditional welsh bara brith", "here is a sample recipe ,this one for the ever popular bara brith.  bara brith ( speckled bread  :", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'cuisine', 'preparation', 'occasion', 'for-1-or-2', 'low-protein', 'healthy', '5-ingredients-or-less', 'breads', 'oven', 'easy', 'european', 'heirloom-historical', 'holiday-event', 'low-fat', 'dietary', 'low-sodium', 'low-cholesterol', 'welsh', 'low-saturated-fat', 'low-calorie', 'low-carb', 'quick-breads', 'low-in-something', 'equipment', 'number-of-servings'", "2427.2, 13.0, 876.0, 160.0, 77.0, 10.0, 185.0", "12", """this is wales' traditional rich fruit bread"", 'south wales makes it with baking powder', 'northerners prefer yeast as the raising agent', ""either way it's delicious"", 'soak the dried fruit and sugar overnight in the tea', 'you can use either fresh tea , or the cold dregs from the teapot', 'next day , sieve the flour and fold it it into the fruit', 'mix in the lightly beaten egg', 'line a small loaf-tin with buttered paper then', 'bake in a gentle oven at 300 f for 1-1 / 2 hours', 'cool and store for at least 2 days in a tin so that it matures moist and rich', ""traditionalists say you should never butter the bara brith , but it's lovely that way !""", "'dried fruit', 'dark brown sugar', 'strong hot tea', 'self-raising flour', 'egg'", "5", "https://www.food.com/recipe/3159");
