INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3164", "kotopoulo me sika  chicken with figs in red wine", "", "55", "'60-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'poultry', 'oven', 'chicken', 'dietary', 'meat', 'whole-chicken', 'equipment'", "645.7, 57.0, 89.0, 7.0, 94.0, 53.0, 10.0", "7", "'arrange the chicken pieces and giblets in a heavy roasting dish', 'sprinkle with the spices and salt and massage into the flesh', 'distribute the onions , garlic , bay leaves and figs over and between the chicken pieces , pour in the wine and cover', 'cook in an oven preheated to 180 c / 350 f / gas 4 for about 50 minutes , until the chicken is barely tender', 'stir in the lemon zest and juice and return to the oven until almost falling from the bones - another 10-i5 minutes', 'if there is an excess of liquid , leave off the cover for this part of the cooking', 'sprinkle with parsley and serve with saffron'", "'chicken', 'coriander seed', 'cayenne pepper', 'cumin', 'black pepper', 'salt', 'onions', 'garlic', 'bay leaves', 'fresh figs', 'mavrothaphne', 'lemon, juice and zest of', 'flat leaf parsley'", "13", "https://www.food.com/recipe/3164");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3165", "an outstanding conch fritters", "here is something i last posted in 1994 i think. these are easily the best fritters i have ever had, and i have had fritters all over south florida and the upper and lower keys. ps: i live in delray beach now, just a bit below palm beach.  ----  finally, here is charlie's pompano beach conch fritters recipe. i have refined this over several years, and most people that have tasted these say they are the best around. this makes about 30 fritters i suppose, enough for apps for five or six people.", "25", "'30-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'healthy', 'appetizers', 'seafood', 'american', 'southern-united-states', 'dinner-party', 'low-fat', 'dietary', 'low-saturated-fat', 'high-calcium', 'high-in-something', 'low-in-something', 'shellfish'", "183.2, 4.0, 8.0, 23.0, 25.0, 3.0, 9.0", "27", "'1 tbsp thyme 1 tbsp parsley 1 tbsp oregano 1 tbsp celery seed 1 tbsp habanero sauce 3 / 4 tbsp salt 1 / 2 tbsp basil 1 / 2 tbsp cumin powder 1 / 2 tsp black pepper', 'pound conch with a rolling pin until it has flattened out , then dice into small pieces', 'mix in lime juice and tomato paste with conch in small bowl', 'in separate large bowl , dice pepper , onion and garlic', 'mix in spices', 'mix in conch mixture', 'mix in flour , baking powder and eggs', 'the mixture should be fairly thick , if you take spoon of it and turn it upside down it should stick there for a few seconds', 'if too thin , add more flour', 'if too thick , add a little milk', 'you could also substitute a little beer for the milk if you like', 'rec get a couple of plates with napkins or paper towels handy , as well as a tablespoon , a teaspoon , a fork , and a metal device for scooping stuff out of hot oil', 'heat a pot of about 2 inches of cooking oil on medium heat', 'stir after a few minutes', ""oil should be ready after 7 or 8 minutes , don't heat too fast or oil and fritters will burn"", 'scoop up 1 tablespoon of fritter mixture , scrape it into oil with teaspoon', 'let cook for about 2 minutes , then roll it over with fork and let cook on other side for about 2 minutes or until lightly browned', 'scoop out with scooper and let drain on napkin plate', 'let cool a bit and then taste', 'carefully adjust seasonings in remainder of batter to taste', 'repeat this procedure of making one fritter until you have the spices just right', 'the recipe may seem like a lot of spices , but they lose their strength when mixed with all that stuff and cooked', 'serve with lemon or lime wedges , some more habenero sauce , and plenty of cold beverages', 'and be careful with that hot oil', 'excessive alcohol consumption and playing around with hot oil definitely do not mix', ""enjoy ! ps: if you don't want to make these fritters but you are in florida , the best that i have found in a re staurant are at the calpyso restaurant and raw bar in pompano"", 'charlie byrne delray beach , fl'", "'conch', 'yellow bell pepper', 'onion', 'garlic clove', 'eggs', 'key lime juice', 'tomato paste', 'flour', 'baking powder', 'milk'", "10", "https://www.food.com/recipe/3165");
