INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3199", "what to do with all the egg yolks bread", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'preparation', 'breads', 'oven', 'dietary', 'equipment', 'number-of-servings'", "531.9, 49.0, 29.0, 15.0, 19.0, 31.0, 17.0", "18", "'butter a 10"" tube pan and set aside', 'in a large mixing bowl , combine the yeast , 1 tsp', 'of the sugar and the warm water', 'set aside for 10 minutes', 'combine the milk , butter , oil , zest , remainider of the sugar and salt', 'stir into the yeast mixture', 'add the egg yolks , stirring well', 'add the flour , 1 / 2 cup at a time , stirring well after each addition to incorporate the flour thoroughly', 'knead 5-10 minutes , until the dough is smooth , elastic and satiny', 'knead in the cran berries and pecans', 'put the dough back in the bowl , cover the bowl and let the dough rise at room temperature until it is doubled in bulk', 'using a wooden spoon , beat down the risen dough for about a minute', 'place the dough into the buttered tube pan and allow it to rise at room temperature until it is doubled in bulk', 'preheat the oven to 375', 'bake the bread for 45-50 minutes or until it is dark golden brown and sounds hollow when tapped', 'place on a rack to cool or serve warm', 'once cooled , the bread is also excellent sliced and toasted', 'recipe by : diane mott davidson , killer pancakes'", "'active dry yeast', 'sugar', 'water', 'skim milk', 'butter', 'canola oil', 'orange zest', 'salt', 'egg yolks', 'all-purpose flour', 'sun-dried cranberries', 'pecans'", "12", "https://www.food.com/recipe/3199");
