INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3201", "holiday season cranberry bread", "we always have a lot of people visiting during the holiday season and it's always nice to have a little something homemade on hand to offer.  this bread is nice to give away as gifts during the holidays.  delicious plain, with butter or with cream cheese (<-- my personal favorite).  thank you mom for this recipe. :)", "75", "'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'breads', 'breakfast', 'fruit', 'oven', 'potluck', 'holiday-event', 'dietary', 'gifts', 'christmas', 'thanksgiving', 'seasonal', 'quick-breads', 'berries', 'brunch', 'to-go', 'equipment', 'number-of-servings', '4-hours-or-less'", "362.6, 20.0, 113.0, 13.0, 13.0, 14.0, 18.0", "10", "'sift flour , sugar , baking powder , salt and soda into large bowl and set aside', 'add 2 tablespoons melted butter to 3 / 4 cup freshly squeezed orange juice', 'add beaten egg to orange mixture and add to dry ingredients', 'mix until dry ingredients are moistened', 'stir in chopped nuts and cranberries', 'turn into 9 x 5 x 3 inch loaf pan', 'bake at 350 degrees for 55 minutes to 1 hour', 'let stand 24 hours before slicing', 'this recipe freezes very well - i usually wrap first in saran wrap , then wrap it in aluminum foil and then place in zip-lock bags - i generally take out of the freezer the night before serving , if possible', 'delicious plain or with butter or cream cheese spread on it'", "'flour', 'sugar', 'baking powder', 'salt', 'baking soda', 'orange juice', 'butter', 'egg', 'fresh cranberries', 'walnuts'", "10", "https://www.food.com/recipe/3201");
