INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3218", "crispy olive   herb focaccia", "a scrumptious version of a classic italian flatbread. basic and delicious!", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'cuisine', 'preparation', 'occasion', 'breads', 'european', 'dinner-party', 'finger-food', 'picnic', 'brunch', 'taste-mood', 'to-go', 'presentation'", "168.7, 6.0, 0.0, 3.0, 7.0, 2.0, 9.0", "15", "'stir the yeast into a large mixing bowl with the water & let proof for 10 minutes', 'stir in the olive oil , rosemary , sage , olives & garlic', 'using a wooden spoon , mix in the flours & salt & stir until the dough is thick & smooth', 'knead by hand for 8 to 10 minutes until the dough is firm & elastic', 'set the dough in a lightly oiled container , cover with plastic wrap & let rise until doubled', 'turn the dough onto an oiled 10 1 / 2"" x 15 1 / 2"" baking pan & stretch it to fit', ""if it won't fit , let it rest for 10 minutes & try again"", 'cover with a towel & leave until it has half risen , about 30 minutes', '30 minutes before baking , preheat oven to 400f', 'just before baking , dimple the top of the dough with your fingerti ps & sprinkle with some extra salt & 2 tsp of oil', 'bake for 25 to 30 minutes until golden', 'slide off baking sheet onto a rack & let cool for a few minutes before eating warm or at room temperature', 'variation: focaccia dolce', 'substitute 2 oz raisins & 1 oz pine nuts for the olives , herbs & garlic', 'soak the raisins in warm water for 30 minutes before using & toast the pine nuts'", "'dried yeast', 'water', 'olive oil', 'fresh rosemary', 'fresh sage leaves', 'olives', 'garlic', 'unbleached all-purpose flour', 'cornflour', 'salt'", "10", "https://www.food.com/recipe/3218");
