INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3267", "elk steaks with green peppercorn sauce", " elk is perhaps the finest of all the venison meats. it is very fine  grained and mild flavoured, almost like beef. like all wild venison,   it is very lean so it benefits from larding, barding and marinading.   with that in mind, you can treat it like beef. steaks are best   grilled, cooked rare or at most medium rare. well done meat will be   tough due to the lack of fat.   ", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'main-dish', 'condiments-etc', 'dietary', 'wild-game', 'elk', 'meat'", "157.4, 14.0, 12.0, 13.0, 2.0, 8.0, 1.0", "14", "'trim any visible fat from the steaks', 'combine the garlic and oil', 'pour over steaks and marinate , refrigerated , overnight', 'season the steaks with salt and freshly ground pepper', 'grill or broil about five-six minutes per side , depending on the heat level of the grill', 'stop while the steak is still quite rare', 'set aside on a heated platter in a low oven until the sauce is ready', 'green peppercorn sauce: in a small saucepan , melt the butter in the oil over high heat until hot but not smoking', 'add onion , and saute , stirring , until tender , about 30 sec', 'add vinegar and wine , and cook , scraping bottom of pan to dissolve residue , until reduced by about half , 2 to 3 min', 'add stock and mustard , and cook , stirring frequently , until reduced by about half , 4 to 6 min', 'remove from heat , stir in peppercorns and correct sea soning', 'drizzle sauce over steaks', 'serve at once'", "'elk steaks', 'garlic', 'vegetable oil', 'salt & pepper', 'butter', 'red onion', 'balsamic vinegar', 'red wine', 'beef stock', 'grainy mustard', 'green peppercorn'", "11", "https://www.food.com/recipe/3267");
