INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3274", "pumpkin cheesecake", "wonderful!", "120", "'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'north-american', 'desserts', 'eggs-dairy', 'vegetables', 'american', 'oven', 'cheesecake', 'dietary', 'equipment', 'number-of-servings', '4-hours-or-less'", "630.8, 61.0, 135.0, 19.0, 19.0, 115.0, 20.0", "21", "'preheat the oven to 350f', 'butter a 9- or 10-inch springform pan and coat lightly with flour in a medium bowl , toss the gingersnap crumbs with the melted butter until evenly moistened', 'press the crumbs into the bottom and 1 inch up the side of the prepared pan', 'bake for about 12 minutes , or until the crust begins to color', 'let the crust cool', 'reduce the oven temperature to 325f', 'in a large bowl , using an electric mixer , beat the cream cheese until smooth', 'beat in 1 / 2 cup of the granulated sugar and the brown sugar , then beat in the eggs in 3 additions until the mixture is thoroughly combined , scraping down the side of the bowl occasionally', 'in a medium bowl , combine the pumpkin puree and heavy cream with 1 teaspoon of the vanilla and the cinnamon , ginger and nutmeg', 'add to the cream cheese mixture and beat until combined , scraping the bowl a few times', 'wrap foil loosely around the bottom and up the side of the springform pan', 'pour the cheesecake batter into the prepared pan and set it in a large baking dish or roasting pan', 'place in the middle of the oven and pour 1 inch of hot water into the baking dish', 'bake the cheesecake for about 70 minutes , or until the edges are firm and the center of the cheesecake is still slightly shaky', 'in a small bowl , combine the sour cream with the remaining 1 / 2 cup of granulated sugar and 2 teaspoons of vanilla', 'remove the cheesecake from the water bath and pour on the sour cream topping', 'gently top the pan to spread the topping , and continue baking the cheesecake for 10 more minutes', 'transfer the cheesecake to a rack and let cool for 1 hour', 'remove the foil and the side of the pan and refrigerate the cheesecake for at least 4 hours or overnight', 'if you like , remove the cheesecake from the bottom of the pan by sliding a metal spatula under the crust to loosen it completely , then use 2 large metal spatulas to transfer the cake to a serving plate', 'alternatively , serve the cheesecake on the pan bottom'", "'gingersnap crumbs', 'unsalted butter', 'cream cheese', 'granulated sugar', 'light brown sugar', 'eggs', 'pumpkin puree', 'heavy cream', 'pure vanilla extract', 'cinnamon', 'ginger', 'nutmeg', 'sour cream'", "13", "https://www.food.com/recipe/3274");
