INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3384", "corn chowder ii", "the original recipe came off the back of a bacon package many years ago, and  i've made it at least 50 times during the intervening years, making adjustments  and refining the recipe. only once did i scorch it. the kids loved it, once i  reduced the pepper amount. after the first time, i've always made  a double  batch.   ", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'low-protein', 'healthy', 'soups-stews', 'vegetables', 'american', 'low-fat', 'chowders', 'dietary', 'low-cholesterol', 'low-saturated-fat', 'low-calorie', 'healthy-2', 'low-in-something', 'corn'", "259.7, 11.0, 25.0, 19.0, 14.0, 13.0, 15.0", "21", "'while the first two ingredients are boiling , fry down the bacon in a large pot', 'remove from heat', 'lift out and drain off the remaining bacon fat', 'return bacon to pot', 'add drained potatoes / onions and remaining ingredients in the pot', 'place over med-low heat', 'heat through', 'stirring often - and serve', '-i use 2 or 3 extra strips of bacon - adds a bit more flavour', 'more flavour is added by the stuff in the bottom of the pot too', '-i use a smaller tin of cream corn and a similar size tin of niblet corn', '-reduce pepper to 1 / 2 teaspoon if kids are involved', '-milk can be increased to 1 cup or more to achieve desired consistency', '-substitute reconstituted powdered milk as a cost sav ing measure', '-to make a richer chowder , use half-and-half for the initial measure of milk', '-this keeps well - if there is any left over !', 'new', 'i had someone write to say how similar this recipe and theirs were', 'the difference was that they boiled the potatoes without the onion , but cooked up the onion with the bacon ""to mix the onions and bacon flavours""', 'they used 1 / 2 pound or more of bacon for the ""double batch""', 'i tried it this way and i am a convert !'", "'potatoes', 'onion', 'bacon', 'creamed corn', 'milk', 'pepper', 'salt'", "7", "https://www.food.com/recipe/3384");
