INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3525", "lazy day lasagna", "when preparing lasagna for the freezer there's no need to precook the noodles.", "85", "'time-to-make', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'pasta', 'oven', 'easy', 'european', 'italian', 'lasagna', 'dietary', 'pasta-rice-and-grains', 'equipment', '4-hours-or-less'", "386.2, 22.0, 48.0, 44.0, 43.0, 35.0, 13.0", "13", "'in two greased 10x6x2-inch baking dishes , make layers in the following order: half each noodles , cottage cheese , mozzarella slices , spaghetti sauce , and sprinkled parmesan cheese', 'repeat', 'make certain the dry noodles are completely covered by sauce', 'wrap pans completely with foil', 'label and freeze', '(to make this meal incredibly rich , add one 8-ounce package cream cheese', 'pinch off nickel-sized portions of cream cheese and plop evenly over lasagna just before adding the second layer of uncooked pasta', ')', 'to serve: to thaw , take meal from freezer at least 24 hours before serving', 'place in refrigerator', 'bake tightly covered at 350 degrees for about 45 minutes , or until edges are bubbly and center is hot', 'take cover off during final 10 minutes of cooking time', 'let stand 10 minutes before serving'", "'lasagna noodles', 'spaghetti sauce', 'cream-style cottage cheese', 'mozzarella cheese', 'parmesan cheese'", "5", "https://www.food.com/recipe/3525");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3526", "lemon shrimp with fresh vegetables", "asparagus, cucumbers and tomatoes are very tender vegetables with sweet fresh flavour, especially when cooked oriental style. to make lemon chicken, substitute cubes or strips of chicken breast for the shrimp.", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'seafood', 'vegetables', 'shrimp', 'dietary', 'low-saturated-fat', 'low-calorie', 'low-in-something', 'shellfish', 'asparagus'", "407.2, 23.0, 173.0, 27.0, 36.0, 12.0, 17.0", "25", "'have all ingredients ready for stir-frying: cut asparagus into 2-inch diagonal pieces', 'cut onion and tomatoes into wedges', 'cut cucumber in half lengthwise , then into 1 / 4-inch chunks', 'peel shrimp', 'lemon sauce: in saucepan , combine sugar , cornstarch , water , lemon juice and vinegar', 'bring to boil , stirring', 'cook 1 or 2 minutes until thickened', 'season to taste with salt , and add a few drops yellow food coloring if desired', 'keep sauce warm while stir-frying', 'in large skillet or wok , heat 2 tbsp oil', 'add asparagus and stir-fry for 1 minute', 'add water and sprinkle lightly with salt', 'cover and steam until asparagus is nearly tender , about 2 minutes', 'remove asparagus and set aside', 'drain skillet', 'add remaining oil to skillet', 'brown onion and ginger lightly', 'remove ginger', 'add shrimp and stir-fry for 2 minutes', 'add cucumber , tomatoes and asparagus', 'cover and steam for 2 or 3 minutes just until tender-crisp', 'taste and adjust seasoning', 'stir in lemon sauce and lemon slices', 'bring to boil , stirring gently', 'serve immediately'", "'peanut oil', 'fresh asparagus', 'water', 'salt', 'onion', 'fresh gingerroot', 'raw shrimp', 'seedless cucumber', 'tomatoes', 'lemon slices', 'sugar', 'cornstarch', 'lemon juice', 'vinegar', 'yellow food coloring'", "15", "https://www.food.com/recipe/3526