INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3715", "iranian eggplant salad a la faride", "here is an eggplant salad that i learned from an iranian jewish woman who took refuge with us for a few months when the government of iran fell to religious  fundamentalists way back in the 70s.", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'cuisine', 'preparation', 'low-protein', 'healthy', 'salads', 'asian', 'middle-eastern', 'low-fat', 'dietary', 'low-sodium', 'low-saturated-fat', 'low-calorie', 'low-carb', 'low-in-something', 'number-of-servings'", "287.0, 9.0, 84.0, 3.0, 29.0, 9.0, 16.0", "15", "'pierce the eggplant all over with a fork and place on cookie sheet', 'bake at 400 - 450-degrees f', 'until soft', 'cool the eggplant', 'scoop it out and set aside', 'do not use the peel', 'in a frying pan saute the onion and the 4 cloves of garlic , chopped', 'when soft , add the scooped out eggplant and stir it around until the eggplant is mushy and the mixture is beginning to dry out', 'add the chopped tomatoes to the mixture in the frying pan', 'when that is all fairly hot and dry add 1 raw , slightly beaten egg', 'nudge this around until the egg is stirred in and set', 'remove from the frying pan', 'add salt and pepper and season with tabasco sauce', 'refrigerate until cold', 'serve on romaine lettuce leaves and eat with either lettuce leaves , corn chips or pita bread'", "'eggplant', 'onion', 'garlic', 'tomatoes', 'egg', 'salt', 'pepper', 'tabasco sauce'", "8", "https://www.food.com/recipe/3715");
