INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3774", "cheesy chicken rolls", "low-fat cheesy chicken rolls", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'poultry', 'oven', 'chicken', 'dietary', 'meat', 'chicken-breasts', 'equipment'", "154.4, 3.0, 9.0, 4.0, 57.0, 3.0, 1.0", "14", "'for filling , in a small bowl combine cheese , mushrooms , the 1 / 4 cup yogurt , chives , parsley , and pimento', 'place 1 chicken breast half , boned side up , between 2 pieces of clear plastic wrap', 'working from the center to the edges , pound lightly with a meat mallet to 1 / 8"" thickness', 'remove plastic wrap', 'sprinkle lightly with salt and pepper', 'spread some of the filling on each chicken breast half', 'fold in the sides and roll up', 'repeat with remaining chicken', 'arrange rolls seam side down in a 10x6x2"" baking dish', 'combine bread crumbs and paprika', 'brush chicken with the 1 tbs yogurt', 'sprin kle with crumb mixture', 'bake in 350 deg f', 'oven for 20-25 minutes or till chicken is tender and no longer pink'", "'chives', 'plain low-fat yogurt', 'parsley', 'mushrooms', 'pimiento strip', 'boneless skinless chicken breast', 'dry breadcrumbs', 'lowfat mozzarella cheese', 'paprika'", "9", "https://www.food.com/recipe/3774");
