INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3933", "oven dried beef jerky", "", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'beef', 'oven', 'dietary', 'meat', 'equipment', 'number-of-servings'", "64.6, 0.0, 12.0, 174.0, 15.0, 0.0, 3.0", "13", "'trim and discard all fat from meat', 'slice meat in long 1 / 4-inch thick strips', 'combine remaining ingredients', 'add meat strips , coating all surfaces', 'the meat will absorb most of the liquid', 'v', 'cover tightly and marinate overnight in refrigerator', 'drain off any liquid', 'arrange meat strips together but not overlapping , directly on oven racks', 'dry meat at lowest oven temperature , 150 degrees to 200 degrees until meat turns brown , is dry and feels hard', 'cut into 1-inch pieces , using scissors', 'cool thoroughly before storing in airtight containers', 'beef jerky keeps indefinitely at cool room temperature or in refrigerator'", "'lean flank steak', 'soy sauce', 'worcestershire sauce', 'pepper', 'garlic powder', 'onion salt'", "6", "https://www.food.com/recipe/3933");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("3934", "chocolate espresso fudge cake", "a smooth glaze sheathes discs of devil's food cake and coffee-flavored ganache. the chocolate espresso fudge sauce echoes the coffee flavor in the ganache, and all is harmony on land and sea.", "60", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'desserts', 'oven', 'picnic', 'chocolate', 'dietary', 'low-sodium', 'low-in-something', 'taste-mood', 'sweet', 'to-go', 'equipment'", "1255.6, 149.0, 203.0, 9.0, 34.0, 299.0, 36.0", "57", """make the devil's food cake: position a rack in the center of the oven and preheat to 350 degrees f"", 'butter the bottom and sides o f a 17 1 / 2 by 11 1 / 2-inch jelly roll pan and line the bottom with baking parchment or waxed paper', 'lightly dust the sides of the pan with flour and tap out the excess', 'in the 4 1 / 2-quart bowl of a heavy-duty electric mixer , place the cake flour , sugar , cocoa powder , and baking powder', 'using the paddle attachment , blend the dry ingredients on low speed , until combined', 'add the butter , one tablespoon at a time and continue to mix until the mixture resembles coarse meal', 'at medium speed , beat in the water , scraping down the side of the bowl as necessary', 'continue to beat the batter at medium speed for 2 minutes', 'beat in the eggs , one at a time , scralling down the sides of the bowl with a rubber spatula after each addition', 'scrape the batter into the prepared pan and , using an offset metal cake spatula , smooth it into an even layer', 'bake the cake for 18 to 22 minutes , until the cake springs back when gently touched in the center', 'run the', 'invert the cake on to a wire rack', 'carefully peel off the paper and cool the cake completely', 'make the chocolate espresso whipped garlache:', 'in a small cup , stir the espresso powder into the hot water until dissolved', 'set aside', 'place the chocolate in a medium bowl: set aside', 'in a small , heavy saucepan , combine the heavy cream and espresso mixture', 'bring to a gentle boil', 'remove from heat and pour the mixture over the chocolate', 'allow the mixt ure to stand for 1 minute to melt the chocolate', 'gently whisk until smooth', 'cover the bowl and refrigerate the ganache for 2 hours , until chilled and slightly thickened', 'using a hand-held electric mixer , beat the ganache just until lightened in color , about 30 seconds', 'do not overbeat the ganache or it will become grainy', ""assemble the cakes: using eight 3-inch metal ring molds , cut out 8 rounds of the devil's food cake"", 'place the molds on a baking sheet and place a cake round in the bottom of each mold', 'fill a pastry bag fitted with a large plain', 'pipe the ganache on top of each round , filling each mold to the top', 'with a small , offset metal spatula , scrape the top of each mold to the level the ganache', 'place the sheet of molds in the freezer for 2 hours', 'make the glaze: place the chocolate in a medium bowl', 'in a small saucepan , bring the cream and corn syrup to a gentle boil', 'pour the hot cream over the chocolate and let the mixture stand for 1 minute to melt the chocolate', 'gently whisk until smooth', 'cover the surface of the glaze with plastic wrap and let it stand at room temperature until ready to glaze the cakes', 'make the chocolate espresso fudge sauce: in a medium saucepan , stir together the cocoa , espresso powder and sugar', 'slowly whisk in the milk and cream', 'stir in the corn syrup', 'cook over medium heat , stirring constantly until the sugar is completely dissolved', 'raise the heat to medium-high and while continuing to stir , bring the mixture to a boil', 'remove the pan from the heat and add the chocolate', 'let the mixture stand for 30 seconds to melt the chocolate', 'whisk until smooth', 'stir in the butter and vanilla', 'unmold and glaze the cakes: remove the molds from the freezer', 'gently heat the side of each mold using a portable hair dryer or a hot , damp towel', 'carefully slide off each mold', 'place the cakes on a wire rack set over a baking sheet', 'place the baking sheet in the freezer for 15 minutes', 'remove the plastic wrap from the glaze and gently whisk until smooth', 'remove the desserts from the freezer and pour the glaze over each cake to coat it completely', 'place a spoonful of chocolate espresso fudge sauce on a dessert plate', 'place a cake on top of the sauce', 'garnish with a candied violet , if desired', 'repeat with the remaining cakes'", "'cake flour', 'granulated sugar', 'unsweetened alkalized cocoa powder', 'baking powder', 'unsalted butter', 'water', 'eggs', 'espresso powder', 'hot water', 'bittersweet chocolate', 'heavy cream', 'semisweet chocolate', 'corn syrup', 'milk', 'light corn syrup', 'vanilla extract', 'candied violet'", "17", "https://www.food.com/recipe/3934");
