INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4002", "crunchy pecan bars", "when marilyn martell started exhibiting at her local county fair, she won so many ribbons for cookies that she decided to share them in a cookbook entitled ""county fair prize cookies"", published in 1975. for a number of years now, she has competed at the state fair, continuing to win blue ribbons for baking. crunchy pecan bars is a long-time favorite. these delectable toffee-flavored bars are quick to make and yield a large batch. simply pat the dough into a jelly-roll pan. long baking at a low temperature gives the thin bars their crisp texture. they keep well-if you hide them.", "60", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'desserts', 'fruit', 'oven', 'cookies-and-brownies', 'bar-cookies', 'nuts', 'dietary', 'equipment', 'number-of-servings'", "63.0, 6.0, 11.0, 0.0, 1.0, 8.0, 1.0", "15", "'preheat oven to 275f', 'butter a 15 x 10 jelly-roll pan', 'in a large bowl , cream butter and sugar until light and fluffy', 'add egg yolk and vanilla', 'mix well', 'in a medium-size bowl , mix flour and cinnamon', 'sfir flour mixture into creamed mixture , using hands to mix stiff dough , if necessary', 'spread evenly in buttered jelly-roll pan', 'in a small bowl , beat egg white just until well mixed', 'spread over dough', 'sprinkle pecans evenly over top , then sprinkie lightly with sugar', 'bake in preheated oven about 1 hour or until set', 'cut in 2 x 1"" bars or diamond shapes while still very warm', 'remove cookies from baking sheet to wire racks and cool', 'cookies become crisp as they cool'", "'butter', 'sugar', 'egg', 'vanilla extract', 'all-purpose flour', 'cinnamon', 'pecans'", "7", "https://www.food.com/recipe/4002");
